import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/CodingChallenge.css';

const CodingChallenge = ({ token, question, onSubmit, onSkip, initialCode = '' }) => {
  const [code, setCode] = useState(initialCode);
  const [selectedLanguage, setSelectedLanguage] = useState('python');
  const [testResults, setTestResults] = useState(null);
  const [compileOutput, setCompileOutput] = useState('');
  const [loading, setLoading] = useState(false);
  const [darkMode, setDarkMode] = useState(localStorage.getItem('codeEditorTheme') === 'dark');
  const [expandedHint, setExpandedHint] = useState(null);
  const [submitted, setSubmitted] = useState(false);
  const [submissionData, setSubmissionData] = useState(null);
  const [showProblem, setShowProblem] = useState(true);

  const programmingLanguages = ['Python', 'JavaScript', 'Java', 'C++', 'Go', 'Rust'];

  // Save theme preference
  useEffect(() => {
    localStorage.setItem('codeEditorTheme', darkMode ? 'dark' : 'light');
  }, [darkMode]);

  const runTests = async () => {
    if (!code.trim()) {
      setCompileOutput('‚ùå Please write some code first!');
      return;
    }

    try {
      setLoading(true);
      setCompileOutput('Running tests...');

      const response = await axios.post(
        `http://localhost:5000/api/interview/${token}/evaluate-code-strict`,
        {
          code: code,
          language: selectedLanguage.toLowerCase(),
          test_cases: question?.test_cases || []
        }
      );

      const evaluation = response.data.evaluation;
      const results = evaluation.test_results || [];

      setTestResults(results);

      // Calculate stats
      const passedCount = results.filter(r => r.passed).length;
      const totalCount = results.length;
      const passPercentage = totalCount > 0 ? (passedCount / totalCount) * 100 : 0;

      if (passedCount === totalCount) {
        setCompileOutput(`‚úÖ All ${totalCount} tests passed!\n\nExecution Time: ${evaluation.execution_time || 'N/A'} ms\nMemory: ${evaluation.memory_usage || 'N/A'} MB`);
      } else {
        setCompileOutput(`‚ö†Ô∏è ${passedCount}/${totalCount} tests passed (${passPercentage.toFixed(0)}%)\n\nFeedback: ${evaluation.feedback || 'Check your logic'}`);
      }
    } catch (error) {
      console.error('Error running tests:', error);
      setCompileOutput('‚ùå Error evaluating code. Please check your syntax.');
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async () => {
    if (!code.trim()) {
      alert('Please write some code before submitting!');
      return;
    }

    try {
      setLoading(true);
      const response = await axios.post(
        `http://localhost:5000/api/interview/${token}/evaluate-code-strict`,
        {
          code: code,
          language: selectedLanguage.toLowerCase(),
          test_cases: question?.test_cases || []
        }
      );

      const evaluation = response.data.evaluation;
      setSubmitted(true);
      setSubmissionData({
        code,
        language: selectedLanguage,
        evaluation,
        timestamp: new Date().toISOString()
      });

      // Call parent callback
      if (onSubmit) {
        onSubmit({
          code,
          language: selectedLanguage,
          evaluation
        });
      }
    } catch (error) {
      console.error('Error submitting code:', error);
      alert('Error submitting code');
    } finally {
      setLoading(false);
    }
  };

  if (!question) {
    return <div className={`coding-container ${darkMode ? 'dark' : 'light'}`}>No question available</div>;
  }

  return (
    <div className={`coding-container ${darkMode ? 'dark' : 'light'}`}>
      {/* Clean Header */}
      <div className="coding-header">
        <div className="header-content">
          <div className="header-title-section">
            <h1 className="problem-title">{question.title || 'Coding Challenge'}</h1>
            <span className={`difficulty-badge ${question.difficulty?.toLowerCase()}`}>
              {question.difficulty}
            </span>
          </div>
          <div className="header-controls">
            <button
              className={`tab-btn ${showProblem ? 'active' : ''}`}
              onClick={() => setShowProblem(true)}
            >
              üìã Problem
            </button>
            <button
              className={`tab-btn ${!showProblem ? 'active' : ''}`}
              onClick={() => setShowProblem(false)}
            >
              üíª Code
            </button>
            <button
              className="theme-toggle"
              onClick={() => setDarkMode(!darkMode)}
              title={darkMode ? 'Light Mode' : 'Dark Mode'}
            >
              {darkMode ? '‚òÄÔ∏è' : 'üåô'}
            </button>
          </div>
        </div>
      </div>

      {/* Main Content - Responsive Layout */}
      <div className="coding-main">
        {/* Problem Panel - Can be hidden on mobile */}
        <div className={`problem-panel ${showProblem ? 'visible' : 'hidden'}`}>
          <div className="panel-content">
            {/* Description */}
            <div className="problem-section">
              <div className="section-title">üìñ Description</div>
              <p className="description">{question.description}</p>
            </div>

            {/* Examples */}
            {question.examples && question.examples.length > 0 && (
              <div className="problem-section">
                <div className="section-title">üìù Examples</div>
                <div className="examples-list">
                  {question.examples.map((example, idx) => (
                    <div key={idx} className="example-block">
                      <div className="example-number">Example {idx + 1}</div>
                      <div className="io-pair">
                        <div className="io-label">Input:</div>
                        <div className="io-code">{JSON.stringify(example.input)}</div>
                      </div>
                      <div className="io-pair">
                        <div className="io-label">Output:</div>
                        <div className="io-code">{JSON.stringify(example.output)}</div>
                      </div>
                      {example.explanation && (
                        <div className="explanation-block">
                          <span className="io-label">Explanation:</span>
                          <p className="explanation-text">{example.explanation}</p>
                        </div>
                      )}
                    </div>
                  ))}
                </div>
              </div>
            )}

            {/* Constraints */}
            {question.constraints && question.constraints.length > 0 && (
              <div className="problem-section">
                <div className="section-title">‚öôÔ∏è Constraints</div>
                <div className="constraints-box">
                  <ul className="constraints-list">
                    {question.constraints.map((constraint, idx) => (
                      <li key={idx}>{constraint}</li>
                    ))}
                  </ul>
                </div>
              </div>
            )}

            {/* Hints */}
            {question.hints && question.hints.length > 0 && (
              <div className="problem-section">
                <div className="section-title">üí° Hints</div>
                <div className="hints-container">
                  {question.hints.map((hint, idx) => (
                    <details key={idx} className="hint-item">
                      <summary onClick={() => setExpandedHint(expandedHint === idx ? null : idx)}>
                        <span className="hint-icon">üìå</span>
                        <span>Hint {idx + 1}</span>
                      </summary>
                      <div className="hint-content">{hint}</div>
                    </details>
                  ))}
                </div>
              </div>
            )}
          </div>
        </div>

        {/* Code Editor Panel */}
        <div className="editor-panel">
          {/* Editor Toolbar */}
          <div className="editor-toolbar">
            <div className="toolbar-left">
              <label className="language-label">Language:</label>
              <select
                value={selectedLanguage}
                onChange={(e) => setSelectedLanguage(e.target.value)}
                disabled={submitted}
                className="language-selector"
              >
                {programmingLanguages.map(lang => (
                  <option key={lang} value={lang}>{lang}</option>
                ))}
              </select>
            </div>
            <div className="toolbar-right">
              <span className="char-count">{code.length} chars</span>
            </div>
          </div>

          {/* Code Editor */}
          <div className="editor-wrapper">
            <textarea
              className="code-editor"
              value={code}
              onChange={(e) => setCode(e.target.value)}
              placeholder={`// Write your ${selectedLanguage} solution here...\n\nfunction solve(input) {\n  // Your code here\n  return output;\n}`}
              disabled={submitted}
              spellCheck="false"
            />
          </div>

          {/* Action Buttons */}
          <div className="button-group">
            <button
              className="btn-run"
              onClick={runTests}
              disabled={loading || submitted || !code.trim()}
            >
              {loading ? '‚è≥ Running...' : '‚ñ∂Ô∏è Run Code'}
            </button>
            <button
              className="btn-submit"
              onClick={handleSubmit}
              disabled={loading || submitted}
            >
              {submitted ? '‚úì Submitted' : 'üöÄ Submit'}
            </button>
            {onSkip && (
              <button
                className="btn-skip"
                onClick={onSkip}
                disabled={submitted}
              >
                Skip
              </button>
            )}
          </div>

          {/* Test Results Section */}
          <div className="results-section">
            {testResults && testResults.length > 0 && (
              <div className="results-container">
                <div className="results-header">
                  <h3>Test Results</h3>
                  <span className="results-badge">
                    {testResults.filter(r => r.passed).length}/{testResults.length} Passed
                  </span>
                </div>
                <div className="test-cases-grid">
                  {testResults.map((result, idx) => (
                    <div key={idx} className={`test-case ${result.passed ? 'passed' : 'failed'}`}>
                      <div className="test-header">
                        <span className="status-icon">{result.passed ? '‚úÖ' : '‚ùå'}</span>
                        <span>Test {idx + 1}</span>
                      </div>
                      <div className="test-details">
                        {!result.passed && (
                          <>
                            <div className="detail-row">
                              <span className="label">Expected:</span>
                              <code>{result.expected || 'N/A'}</code>
                            </div>
                            <div className="detail-row error-row">
                              <span className="label">Got:</span>
                              <code>{result.actual || 'N/A'}</code>
                            </div>
                          </>
                        )}
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            )}

            {/* Output Console */}
            {compileOutput && (
              <div className={`output-console ${testResults?.some(r => !r.passed) ? 'error-state' : 'success-state'}`}>
                <div className="console-label">Output</div>
                <pre className="console-output">{compileOutput}</pre>
              </div>
            )}

            {!compileOutput && !testResults && !submitted && (
              <div className="empty-state">
                <div className="empty-icon">‚ñ∂Ô∏è</div>
                <div className="empty-text">Click "Run Code" to test your solution</div>
              </div>
            )}

            {submitted && !testResults && (
              <div className="success-state-message">
                <div className="success-icon">‚ú®</div>
                <h3>Submitted Successfully!</h3>
                <p>Your solution has been evaluated.</p>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default CodingChallenge;
