import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import '../styles/InterviewSession.css';

const InterviewSession = ({ token }) => {
  const [interviewData, setInterviewData] = useState(null);
  const [currentPhase, setCurrentPhase] = useState('welcome');
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [responses, setResponses] = useState({});
  const [phaseResponses, setPhaseResponses] = useState([]);
  const [codingCode, setCodingCode] = useState('');
  const [codingResult, setCodingResult] = useState(null);
  const [timeRemaining, setTimeRemaining] = useState(0);
  const [phaseTimeRemaining, setPhaseTimeRemaining] = useState(0);
  const [status, setStatus] = useState('loading');
  const [score, setScore] = useState(null);
  const [loading, setLoading] = useState(false);
  const [phaseStarted, setPhaseStarted] = useState(false);
  const [phaseQuestions, setPhaseQuestions] = useState([]);
  const [phaseAssessment, setPhaseAssessment] = useState(null);
  const [followUpQuestion, setFollowUpQuestion] = useState(null);
  const [showFollowUp, setShowFollowUp] = useState(false);
  const [phaseDepthLevel, setPhaseDepthLevel] = useState(0);
  const timerRef = useRef(null);
  const phaseTimerRef = useRef(null);
  
  // Phases configuration
  const phases = [
    { id: 'welcome', label: 'Welcome', duration: 0 },
    { id: 'instruction', label: 'Instructions', duration: 0 }, 
    { id: 'behavioral', label: 'Behavioral Questions', duration: 20 * 60 },
    { id: 'technical', label: 'Technical Questions', duration: 20 * 60 },
    { id: 'coding', label: 'Coding Challenge', duration: 20 * 60 },
    { id: 'completion', label: 'Completion', duration: 0 }
  ];

  useEffect(() => {
    fetchInterviewStatus();
  }, [token]);

  useEffect(() => {
    if (phaseStarted && currentPhase !== 'welcome' && currentPhase !== 'completion') {
      const phaseDuration = phases.find(p => p.id === currentPhase)?.duration || 0;
      setPhaseTimeRemaining(phaseDuration);
      startPhaseTimer(phaseDuration);
    }
  }, [phaseStarted, currentPhase]);

  const fetchInterviewStatus = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/interview/${token}/status`);
      setInterviewData(response.data);
      setStatus(response.data.status);
      
      if (response.data.status === 'completed') {
        setScore(response.data.interview_score);
      }
    } catch (error) {
      console.error('Error fetching interview:', error);
      setStatus('error');
    }
  };

  const startInterview = async () => {
    try {
      setLoading(true);
      const response = await axios.post(`http://localhost:5000/api/interview/${token}/start`);
      setInterviewData(response.data);
      setStatus('in_progress');
      
      setTimeRemaining(65 * 60);
      startTimer();
      
      setCurrentPhase('instruction');
      setPhaseStarted(true);
    } catch (error) {
      console.error('Error starting interview:', error);
      alert('Failed to start interview');
    } finally {
      setLoading(false);
    }
  };

  const startTimer = () => {
    if (timerRef.current) clearInterval(timerRef.current);
    timerRef.current = setInterval(() => {
      setTimeRemaining(prev => {
        if (prev <= 1) {
          clearInterval(timerRef.current);
          completeInterview();
          return 0;
        }
        return prev - 1;
      });
    }, 1000);
  };

  const startPhaseTimer = (duration) => {
    if (phaseTimerRef.current) clearInterval(phaseTimerRef.current);
    phaseTimerRef.current = setInterval(() => {
      setPhaseTimeRemaining(prev => {
        if (prev <= 1) {
          clearInterval(phaseTimerRef.current);
          handlePhaseComplete();
          return 0;
        }
        return prev - 1;
      });
    }, 1000);
  };

  const handlePhaseComplete = () => {
    submitPhaseAssessment();
    moveToNextPhase();
  };

  const moveToNextPhase = () => {
    if (phaseTimerRef.current) clearInterval(phaseTimerRef.current);
    
    const currentPhaseIndex = phases.findIndex(p => p.id === currentPhase);
    if (currentPhaseIndex < phases.length - 1) {
      const nextPhase = phases[currentPhaseIndex + 1];
      setCurrentPhase(nextPhase.id);
      setCurrentQuestionIndex(0);
      setPhaseResponses([]);
      if (nextPhase.id !== 'completion' && nextPhase.id !== 'welcome') {
        setPhaseStarted(true);
        generatePhaseQuestions(nextPhase.id);
      }
    } else {
      completeInterview();
    }
  };

  const generatePhaseQuestions = async (phaseType) => {
    try {
      const jobTitle = interviewData?.job_title || 'Software Engineer';
      const candidateSkills = interviewData?.candidate_skills || [];
      
      const response = await axios.post(
        `http://localhost:5000/api/interview/${token}/generate-questions`,
        {
          job_title: jobTitle,
          candidate_skills: candidateSkills,
          interview_type: phaseType
        }
      );
      
      if (response.data.success) {
        setPhaseQuestions(response.data.questions || []);
      }
    } catch (error) {
      console.error('Error generating questions:', error);
      setPhaseQuestions(interviewData?.questions || []);
    }
  };

  const getFollowUpQuestion = async () => {
    try {
      const currentQ = phaseQuestions[currentQuestionIndex];
      const currentResponse = responses[currentQuestionIndex];
      
      if (!currentQ || !currentResponse) return;
      
      setLoading(true);
      const response = await axios.post(
        `http://localhost:5000/api/interview/${token}/follow-up-question`,
        {
          original_question: currentQ.question,
          candidate_response: currentResponse,
          interview_type: currentPhase,
          depth_level: phaseDepthLevel + 1
        }
      );
      
      if (response.data.success) {
        setFollowUpQuestion(response.data);
        setShowFollowUp(true);
        setPhaseDepthLevel(response.data.depth_level);
      }
    } catch (error) {
      console.error('Error getting follow-up:', error);
    } finally {
      setLoading(false);
    }
  };

  const submitPhaseAssessment = async () => {
    try {
      const responsesToSubmit = phaseQuestions.map((q, idx) => ({
        question: q.question,
        response: responses[idx] || '',
        question_id: q.id
      }));
      
      const response = await axios.post(
        `http://localhost:5000/api/interview/${token}/assess-responses`,
        {
          responses: responsesToSubmit,
          interview_type: currentPhase
        }
      );
      
      if (response.data.success) {
        setPhaseAssessment(response.data.assessment);
      }
    } catch (error) {
      console.error('Error assessing responses:', error);
    }
  };

  const handlePhaseNavigation = (phaseId) => {
    const currentPhaseIndex = phases.findIndex(p => p.id === currentPhase);
    const targetPhaseIndex = phases.findIndex(p => p.id === phaseId);
    
    if (targetPhaseIndex > currentPhaseIndex + 1) {
      alert('Please complete the current phase before moving to the next one.');
      return;
    }
    
    if (targetPhaseIndex < currentPhaseIndex) {
      alert('You cannot go back to previous phases.');
      return;
    }
    
    if (targetPhaseIndex === currentPhaseIndex + 1) {
      setCurrentPhase(phaseId);
      setCurrentQuestionIndex(0);
      setPhaseResponses([]);
      setPhaseStarted(true);
      generatePhaseQuestions(phaseId);
    }
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  const handleResponseChange = (e) => {
    setResponses({
      ...responses,
      [currentQuestionIndex]: e.target.value
    });
  };

  const submitCurrentResponse = async () => {
    const response = responses[currentQuestionIndex] || '';
    
    if (!response.trim()) {
      alert('Please provide a response before submitting');
      return;
    }

    try {
      setLoading(true);
      const questionId = phaseQuestions[currentQuestionIndex]?.id || currentQuestionIndex;
      
      await axios.post(`http://localhost:5000/api/interview/${token}/submit-response`, {
        question_id: questionId,
        response: response
      });

      // Add to phase responses
      const newPhaseResponses = [...phaseResponses];
      newPhaseResponses.push({
        question: phaseQuestions[currentQuestionIndex]?.question || `Question ${currentQuestionIndex + 1}`,
        response: response
      });
      setPhaseResponses(newPhaseResponses);

      // Move to next question
      if (currentQuestionIndex < (phaseQuestions.length - 1)) {
        setCurrentQuestionIndex(currentQuestionIndex + 1);
        setShowFollowUp(false);
        setPhaseDepthLevel(0);
      } else {
        // Phase complete
        alert('All questions answered for this phase!');
        await submitPhaseAssessment();
      }
    } catch (error) {
      console.error('Error submitting response:', error);
      alert('Failed to submit response');
    } finally {
      setLoading(false);
    }
  };

  const submitCodingSolution = async () => {
    if (!codingCode.trim()) {
      alert('Please write some code before submitting');
      return;
    }

    try {
      setLoading(true);
      const response = await axios.post(`http://localhost:5000/api/interview/${token}/submit-code`, {
        code: codingCode,
        language: 'python'
      });

      setCodingResult(response.data.evaluation);
      alert(`Code submitted! Score: ${response.data.evaluation.overall_score.toFixed(2)}%`);
    } catch (error) {
      console.error('Error submitting code:', error);
      alert('Failed to submit code');
    } finally {
      setLoading(false);
    }
  };

  const completeInterview = async () => {
    try {
      const response = await axios.post(`http://localhost:5000/api/interview/${token}/complete`);
      setStatus('completed');
      setScore(response.data.interview_score);
    } catch (error) {
      console.error('Error completing interview:', error);
    }
  };

  if (status === 'loading') {
    return (
      <div className="interview-wrapper loading-state">
        <div className="spinner"></div>
        <p>Loading your interview...</p>
      </div>
    );
  }

  if (status === 'error') {
    return (
      <div className="interview-wrapper error-state">
        <div className="error-box">
          <h2>Interview Not Found</h2>
          <p>The interview link may be expired or invalid.</p>
          <a href="/" className="btn btn-primary">Return Home</a>
        </div>
      </div>
    );
  }

  if ((status === 'generated' || status === 'pending') && !loading) {
    return (
      <div className="interview-wrapper welcome-state">
        <div className="welcome-box">
          <h1>Interview Session</h1>
          <p className="candidate-name">{interviewData?.candidate_name}</p>
          
          <div className="info-grid">
            <div className="info-card">
              <div className="info-label">Duration</div>
              <div className="info-value">65 minutes</div>
            </div>
            <div className="info-card">
              <div className="info-label">Questions</div>
              <div className="info-value">7 Total</div>
            </div>
            <div className="info-card">
              <div className="info-label">Format</div>
              <div className="info-value">AI-Powered</div>
            </div>
          </div>

          <div className="preparation-section">
            <h3>Before You Start</h3>
            <ul>
              <li>Answer technical and behavioral questions clearly</li>
              <li>Complete a coding challenge</li>
              <li>Receive instant evaluation and feedback</li>
              <li>Time is tracked automatically</li>
            </ul>
          </div>

          <button 
            className="btn btn-primary full-width"
            onClick={startInterview}
            disabled={loading}
          >
            {loading ? 'Starting...' : 'Start Interview'}
          </button>
        </div>
      </div>
    );
  }

  if (status === 'in_progress') {
    return (
      <div className="interview-wrapper interview-state">
        {/* Phase Navigation Sidebar */}
        <div className="interview-sidebar">
          <div className="sidebar-header">
            <div className="interview-progress-ring">
              <svg viewBox="0 0 100 100" className="progress-ring-svg">
                <circle cx="50" cy="50" r="45" className="progress-ring-bg"/>
                <circle cx="50" cy="50" r="45" className="progress-ring-fill"
                  style={{strokeDashoffset: 282 - (282 * ((65 * 60 - timeRemaining) / (65 * 60)))}}
                />
              </svg>
              <div className="progress-text">{formatTime(timeRemaining)}</div>
            </div>
            <h3 className="sidebar-title">Interview Phases</h3>
          </div>

          <div className="phase-list">
            {phases.map((phase, idx) => {
              const isActive = phase.id === currentPhase;
              const isCompleted = phases.findIndex(p => p.id === currentPhase) > idx;
              const isLocked = phases.findIndex(p => p.id === currentPhase) < idx;
              
              return (
                <div
                  key={phase.id}
                  className={`phase-item ${isActive ? 'active' : ''} ${isCompleted ? 'completed' : ''} ${isLocked ? 'locked' : ''}`}
                  onClick={() => !isLocked && phase.id !== currentPhase && handlePhaseNavigation(phase.id)}
                >
                  <div className="phase-indicator">
                    {isCompleted ? <span></span> : <span>{idx + 1}</span>}
                  </div>
                  <div className="phase-info">
                    <div className="phase-name">{phase.label}</div>
                    {phase.duration > 0 && (
                      <div className="phase-duration">{Math.floor(phase.duration / 60)}m</div>
                    )}
                  </div>
                </div>
              );
            })}
          </div>
        </div>

        {/* Main Content Area */}
        <div className="interview-main">
          {/* Header with Current Phase */}
          <div className="interview-header-new">
            <div className="header-left">
              <div className="candidate-info-compact">
                <h3>{interviewData?.candidate_name}</h3>
                <p className="current-phase">{phases.find(p => p.id === currentPhase)?.label}</p>
              </div>
            </div>
            <div className="header-right">
              <div className="phase-timer">
                <span className="timer-label">Phase Time</span>
                <span className="timer-value">{formatTime(phaseTimeRemaining)}</span>
              </div>
            </div>
          </div>

          {/* Welcome Phase */}
          {currentPhase === 'welcome' && (
            <div className="phase-content welcome-phase">
              <div className="phase-icon welcome-icon"></div>
              <h2>Welcome to Your AI-Powered Interview</h2>
              <p className="phase-subtitle">Showcase your skills across multiple dimensions</p>
              
              <div className="interview-overview">
                <div className="overview-card">
                  <div className="card-icon"></div>
                  <h4>Structured Format</h4>
                  <p>Progress through behavioral questions, technical questions, and a coding challenge in a guided sequence.</p>
                </div>
                <div className="overview-card">
                  <div className="card-icon"></div>
                  <h4>65 Minutes Total</h4>
                  <p>Behavioral (20m)  Technical (20m)  Coding (20m)  Review (5m)</p>
                </div>
                <div className="overview-card">
                  <div className="card-icon"></div>
                  <h4>AI-Powered Evaluation</h4>
                  <p>Get instant feedback and comprehensive evaluation powered by advanced AI systems.</p>
                </div>
              </div>

              <div className="interview-rules">
                <h4>Important Guidelines</h4>
                <ul>
                  <li> Answer all questions honestly and thoughtfully</li>
                  <li> No skipping to future phases - complete each stage in order</li>
                  <li> Time automatically moves to next phase when exceeded</li>
                  <li> Your responses are automatically saved</li>
                  <li> Do not refresh the page or navigate away</li>
                </ul>
              </div>

              <button 
                className="btn btn-primary btn-large"
                onClick={startInterview}
                disabled={loading}
              >
                {loading ? 'Starting Interview...' : 'Start Interview'}
              </button>
            </div>
          )}

          {/* Instruction Phase */}
          {currentPhase === 'instruction' && !phaseStarted && (
            <div className="phase-content instruction-phase">
              <div className="phase-icon instruction-icon"></div>
              <h2>Interview Instructions</h2>
              <p className="phase-subtitle">Everything you need to know before you begin</p>
              
              <div className="instructions-container">
                <div className="instruction-section">
                  <h4> Behavioral Questions (Next)</h4>
                  <p>Answer real-world scenarios and situational questions. Share your experiences and problem-solving approach.</p>
                  <ul>
                    <li>Be specific with examples</li>
                    <li>Explain your thought process</li>
                    <li>Highlight teamwork and communication</li>
                    <li>Duration: 20 minutes (~3 questions)</li>
                  </ul>
                </div>

                <div className="instruction-section">
                  <h4> Technical Questions</h4>
                  <p>Demonstrate your technical knowledge and problem-solving skills in your area of expertise.</p>
                  <ul>
                    <li>Answer with technical depth</li>
                    <li>Explain design decisions</li>
                    <li>Consider edge cases</li>
                    <li>Duration: 20 minutes (~3 questions)</li>
                  </ul>
                </div>

                <div className="instruction-section">
                  <h4> Coding Challenge</h4>
                  <p>Solve a coding problem. You can use any approach, but clean and efficient code is preferred.</p>
                  <ul>
                    <li>Write working, tested code</li>
                    <li>Optimize for readability</li>
                    <li>Consider time and space complexity</li>
                    <li>Duration: 20 minutes</li>
                  </ul>
                </div>
              </div>

              <div className="tips-section">
                <h4> Tips for Success</h4>
                <div className="tips-grid">
                  <div className="tip-card">
                    <strong>Pace Yourself</strong>
                    <p>You have allocated time for each phase. Use it wisely.</p>
                  </div>
                  <div className="tip-card">
                    <strong>Be Authentic</strong>
                    <p>Answer genuinely. Authenticity resonates more than perfect answers.</p>
                  </div>
                  <div className="tip-card">
                    <strong>Think Out Loud</strong>
                    <p>Explain your reasoning, not just the final answer.</p>
                  </div>
                  <div className="tip-card">
                    <strong>No Pressure</strong>
                    <p>The system evaluates your overall approach, not perfection.</p>
                  </div>
                </div>
              </div>

              <button
                className="btn btn-primary btn-large"
                onClick={() => handlePhaseNavigation('behavioral')}
              >
                Proceed to Behavioral Questions
              </button>
            </div>
          )}

          {/* Behavioral Questions Phase */}
          {currentPhase === 'behavioral' && phaseStarted && (
            <div className="phase-content questions-phase">
              <div className="question-progress">
                <div className="progress-header">
                  <div className="question-counter">
                    <span className="counter-label">Question</span>
                    <span className="counter-number">{currentQuestionIndex + 1}</span>
                    <span className="counter-label">of {interviewData?.questions?.length || 0}</span>
                  </div>
                  <div className="progress-bar-new">
                    <div className="progress-fill-new" 
                      style={{width: `${((currentQuestionIndex + 1) / (interviewData?.questions?.length || 1)) * 100}%`}}
                    ></div>
                  </div>
                </div>
              </div>

              <div className="question-card">
                <div className="question-badge">Behavioral Question</div>
                <h3>{interviewData?.questions?.[currentQuestionIndex]?.question}</h3>

                {interviewData?.questions?.[currentQuestionIndex]?.context && (
                  <div className="context-box-new">
                    <strong>Context:</strong>
                    <p>{interviewData.questions[currentQuestionIndex].context}</p>
                  </div>
                )}

                <div className="response-section">
                  <label>Your Response</label>
                  <textarea
                    className="response-textarea"
                    placeholder="Share your experience, approach, and results. Be specific with examples..."
                    value={responses[currentQuestionIndex] || ''}
                    onChange={handleResponseChange}
                  />
                  <div className="textarea-footer">
                    <span className="char-count">{(responses[currentQuestionIndex] || '').length} characters</span>
                    <span className={`char-hint ${(responses[currentQuestionIndex] || '').length < 50 ? 'too-short' : 'good'}`}>
                      {(responses[currentQuestionIndex] || '').length < 50 ? 'Write more detail...' : 'Great!'}
                    </span>
                  </div>
                </div>

                <div className="action-buttons">
                  <button
                    className="btn btn-outline"
                    onClick={() => setCurrentQuestionIndex(Math.max(0, currentQuestionIndex - 1))}
                    disabled={currentQuestionIndex === 0 || loading}
                  >
                     Previous
                  </button>
                  
                  {currentQuestionIndex < (interviewData?.questions?.length - 1) ? (
                    <button
                      className="btn btn-primary"
                      onClick={submitCurrentResponse}
                      disabled={loading}
                    >
                      {loading ? 'Saving...' : 'Next Question '}
                    </button>
                  ) : (
                    <button
                      className="btn btn-success"
                      onClick={() => {
                        submitCurrentResponse();
                        // Auto move to technical phase after last behavioral question
                        setTimeout(() => handlePhaseNavigation('technical'), 500);
                      }}
                      disabled={loading}
                    >
                      Complete Phase 
                    </button>
                  )}
                </div>
              </div>
            </div>
          )}

          {/* Technical Questions Phase */}
          {currentPhase === 'technical' && phaseStarted && (
            <div className="phase-content questions-phase">
              <div className="question-progress">
                <div className="progress-header">
                  <div className="question-counter">
                    <span className="counter-label">Question</span>
                    <span className="counter-number">{currentQuestionIndex + 1}</span>
                    <span className="counter-label">of {interviewData?.questions?.length || 0}</span>
                  </div>
                  <div className="progress-bar-new">
                    <div className="progress-fill-new" 
                      style={{width: `${((currentQuestionIndex + 1) / (interviewData?.questions?.length || 1)) * 100}%`}}
                    ></div>
                  </div>
                </div>
              </div>

              <div className="question-card">
                <div className="question-badge technical">Technical Question</div>
                <h3>{interviewData?.questions?.[currentQuestionIndex]?.question}</h3>

                {interviewData?.questions?.[currentQuestionIndex]?.context && (
                  <div className="context-box-new">
                    <strong>Context:</strong>
                    <p>{interviewData.questions[currentQuestionIndex].context}</p>
                  </div>
                )}

                <div className="response-section">
                  <label>Your Answer</label>
                  <textarea
                    className="response-textarea"
                    placeholder="Provide a technical explanation. Include architecture, design patterns, or implementation details..."
                    value={responses[currentQuestionIndex] || ''}
                    onChange={handleResponseChange}
                  />
                  <div className="textarea-footer">
                    <span className="char-count">{(responses[currentQuestionIndex] || '').length} characters</span>
                  </div>
                </div>

                <div className="action-buttons">
                  <button
                    className="btn btn-outline"
                    onClick={() => setCurrentQuestionIndex(Math.max(0, currentQuestionIndex - 1))}
                    disabled={currentQuestionIndex === 0 || loading}
                  >
                     Previous
                  </button>
                  
                  {currentQuestionIndex < (interviewData?.questions?.length - 1) ? (
                    <button
                      className="btn btn-primary"
                      onClick={submitCurrentResponse}
                      disabled={loading}
                    >
                      {loading ? 'Saving...' : 'Next Question '}
                    </button>
                  ) : (
                    <button
                      className="btn btn-success"
                      onClick={() => {
                        submitCurrentResponse();
                        setTimeout(() => handlePhaseNavigation('coding'), 500);
                      }}
                      disabled={loading}
                    >
                      Move to Coding Challenge 
                    </button>
                  )}
                </div>
              </div>
            </div>
          )}

          {/* Coding Challenge Phase */}
          {currentPhase === 'coding' && phaseStarted && (
            <div className="phase-content coding-phase">
              <div className="coding-header">
                <div className="problem-info">
                  <div className="badge-container">
                    <span className="badge difficulty-medium">Medium</span>
                  </div>
                  <h3>Coding Challenge: Two Sum Problem</h3>
                </div>
              </div>

              <div className="coding-container">
                <div className="problem-panel">
                  <div className="problem-title">Problem Description</div>
                  <div className="problem-content">
                    <p>Given an array of integers <code>nums</code> and an integer <code>target</code>, return the <strong>indices</strong> of the two numbers that add up to the target.</p>
                    
                    <p><strong>Constraints:</strong></p>
                    <ul className="constraints-list">
                      <li>2  nums.length  10</li>
                      <li>-10  nums[i]  10</li>
                      <li>-10  target  10</li>
                      <li>Only one valid answer exists</li>
                    </ul>

                    <p><strong>Example:</strong></p>
                    <div className="code-example">
                      <code>Input: nums = [2,7,11,15], target = 9<br/>Output: [0,1]<br/>Explanation: nums[0] + nums[1] == 9, return [0, 1]</code>
                    </div>
                  </div>
                </div>

                <div className="solution-panel">
                  <div className="solution-header">
                    <div className="solution-title">Your Solution</div>
                    <span className="language-tag">Python</span>
                  </div>
                  
                  <textarea
                    className="code-editor"
                    placeholder="def twoSum(nums, target):\n    # Your solution here\n    pass"
                    value={codingCode}
                    onChange={(e) => setCodingCode(e.target.value)}
                    spellCheck="false"
                  />

                  {codingResult && (
                    <div className={`evaluation-result ${codingResult.overall_score >= 70 ? 'success' : 'needs-improvement'}`}>
                      <div className="result-header">
                        <span className="result-label">Evaluation Result</span>
                        <span className="result-score">{codingResult.overall_score.toFixed(0)}%</span>
                      </div>
                      <div className="result-body">
                        <div className="result-item">
                          <span>Tests Passed</span>
                          <strong>{codingResult.passed_tests}/{codingResult.total_tests}</strong>
                        </div>
                        <div className="result-item">
                          <span>Code Quality</span>
                          <strong>{codingResult.code_quality_score?.toFixed(0)}%</strong>
                        </div>
                      </div>
                    </div>
                  )}

                  <div className="action-buttons">
                    <button
                      className="btn btn-outline"
                      onClick={() => {
                        setCodingCode('');
                        setCodingResult(null);
                      }}
                    >
                      Clear Code
                    </button>
                    
                    <button
                      className="btn btn-primary"
                      onClick={submitCodingSolution}
                      disabled={loading || !codingCode.trim()}
                    >
                      {loading ? 'Evaluating...' : 'Submit & Evaluate'}
                    </button>
                  </div>
                </div>
              </div>

              <div className="coding-actions">
                <button
                  className="btn btn-success btn-large"
                  onClick={() => handlePhaseNavigation('completion')}
                  disabled={!codingResult || loading}
                >
                  Proceed to Completion
                </button>
              </div>
            </div>
          )}

          {/* Completion Phase */}
          {currentPhase === 'completion' && (
            <div className="phase-content completion-phase">
              <div className="completion-card">
                <div className="completion-icon"></div>
                <h2>Interview Almost Complete!</h2>
                <p className="completion-subtitle">Review and submit your responses</p>

                <div className="completion-summary">
                  <div className="summary-item">
                    <div className="summary-label">Behavioral Questions</div>
                    <div className="summary-status completed"> Completed</div>
                  </div>
                  <div className="summary-item">
                    <div className="summary-label">Technical Questions</div>
                    <div className="summary-status completed"> Completed</div>
                  </div>
                  <div className="summary-item">
                    <div className="summary-label">Coding Challenge</div>
                    <div className="summary-status completed"> Completed</div>
                  </div>
                </div>

                <button
                  className="btn btn-primary btn-large"
                  onClick={completeInterview}
                  disabled={loading}
                >
                  {loading ? 'Finalizing...' : 'Complete Interview'}
                </button>
              </div>
            </div>
          )}
        </div>
      </div>
    );
  }

  if (status === 'completed') {
    return (
      <div className="interview-wrapper completed-state">
        <div className="completion-container">
          <div className="score-circle">
            {score?.toFixed(2) || 'N/A'}%
          </div>

          <h1>Interview Completed</h1>
          <p className="completion-message">Thank you for completing the interview.</p>

          <div className="breakdown-section">
            <h3>Results Summary</h3>
            <div className="breakdown-grid">
              <div className="breakdown-item">
                <span className="label">Coding Ability</span>
                <div className="score-bar">
                  <div className="bar-fill" style={{width: '75%'}}></div>
                </div>
                <span className="value">75%</span>
              </div>
              <div className="breakdown-item">
                <span className="label">Response Quality</span>
                <div className="score-bar">
                  <div className="bar-fill" style={{width: '70%'}}></div>
                </div>
                <span className="value">70%</span>
              </div>
              <div className="breakdown-item">
                <span className="label">Communication</span>
                <div className="score-bar">
                  <div className="bar-fill" style={{width: '72%'}}></div>
                </div>
                <span className="value">72%</span>
              </div>
              <div className="breakdown-item">
                <span className="label">Behavioral Fit</span>
                <div className="score-bar">
                  <div className="bar-fill" style={{width: '68%'}}></div>
                </div>
                <span className="value">68%</span>
              </div>
            </div>
          </div>

          <div className="next-action">
            <h3>What's Next?</h3>
            <p>Our team will review your response within 24-48 hours. Check your email for updates on the next steps.</p>
          </div>

          <a href="/" className="btn btn-primary full-width">
            Return Home
          </a>
        </div>
      </div>
    );
  }

  return (
    <div className="interview-wrapper error-state">
      <div className="error-box">
        <h2>Something Went Wrong</h2>
        <p>Status: {status}</p>
        <p>Please check the interview link and try again.</p>
        <a href="/" className="btn btn-primary">Return Home</a>
      </div>
    </div>
  );
};

export default InterviewSession;
