import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import '../styles/SpeechTest.css';

const SpeechTest = ({ token, questions, onComplete, timeRemaining, formatTime }) => {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [responses, setResponses] = useState({});
  const [isRecording, setIsRecording] = useState(false);
  const [transcript, setTranscript] = useState('');
  const [isProcessing, setIsProcessing] = useState(false);
  const [recordingTime, setRecordingTime] = useState(0);
  const [audioBlob, setAudioBlob] = useState(null);
  const [evaluation, setEvaluation] = useState(null);
  const [speechSupported, setSpeechSupported] = useState(true);

  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);
  const recognitionRef = useRef(null);
  const recordingTimerRef = useRef(null);

  const currentQuestion = questions[currentQuestionIndex];
  const totalQuestions = questions.length;

  // Initialize Web Speech API
  useEffect(() => {
    if ('webkitSpeechRecognition' in window || 'SpeechRecognition' in window) {
      const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
      recognitionRef.current = new SpeechRecognition();
      recognitionRef.current.continuous = true;
      recognitionRef.current.interimResults = true;
      recognitionRef.current.lang = 'en-US';

      recognitionRef.current.onresult = (event) => {
        let finalTranscript = '';
        let interimTranscript = '';

        for (let i = event.resultIndex; i < event.results.length; i++) {
          const result = event.results[i];
          if (result.isFinal) {
            finalTranscript += result[0].transcript + ' ';
          } else {
            interimTranscript += result[0].transcript;
          }
        }

        setTranscript(prev => {
          const newTranscript = prev + finalTranscript;
          return newTranscript || interimTranscript;
        });
      };

      recognitionRef.current.onerror = (event) => {
        console.error('Speech recognition error:', event.error);
        if (event.error === 'not-allowed') {
          setSpeechSupported(false);
        }
      };

      recognitionRef.current.onend = () => {
        if (isRecording) {
          // Restart if still recording
          try {
            recognitionRef.current.start();
          } catch (e) {
            console.log('Recognition already started');
          }
        }
      };
    } else {
      setSpeechSupported(false);
    }

    return () => {
      if (recognitionRef.current) {
        recognitionRef.current.stop();
      }
      if (recordingTimerRef.current) {
        clearInterval(recordingTimerRef.current);
      }
    };
  }, []);

  // Recording timer
  useEffect(() => {
    if (isRecording) {
      recordingTimerRef.current = setInterval(() => {
        setRecordingTime(prev => prev + 1);
      }, 1000);
    } else {
      if (recordingTimerRef.current) {
        clearInterval(recordingTimerRef.current);
      }
    }

    return () => {
      if (recordingTimerRef.current) {
        clearInterval(recordingTimerRef.current);
      }
    };
  }, [isRecording]);

  const startRecording = async () => {
    try {
      // Reset states
      setTranscript('');
      setRecordingTime(0);
      setAudioBlob(null);
      setEvaluation(null);
      audioChunksRef.current = [];

      // Request microphone access
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      
      // Start media recorder for audio blob
      mediaRecorderRef.current = new MediaRecorder(stream);
      
      mediaRecorderRef.current.ondataavailable = (event) => {
        if (event.data.size > 0) {
          audioChunksRef.current.push(event.data);
        }
      };

      mediaRecorderRef.current.onstop = () => {
        const blob = new Blob(audioChunksRef.current, { type: 'audio/webm' });
        setAudioBlob(blob);
        stream.getTracks().forEach(track => track.stop());
      };

      mediaRecorderRef.current.start(1000); // Collect data every second

      // Start speech recognition
      if (recognitionRef.current) {
        recognitionRef.current.start();
      }

      setIsRecording(true);
    } catch (error) {
      console.error('Error starting recording:', error);
      alert('Could not access microphone. Please check permissions.');
    }
  };

  const stopRecording = () => {
    setIsRecording(false);

    if (mediaRecorderRef.current && mediaRecorderRef.current.state !== 'inactive') {
      mediaRecorderRef.current.stop();
    }

    if (recognitionRef.current) {
      recognitionRef.current.stop();
    }
  };

  const submitAnswer = async () => {
    if (!transcript.trim()) {
      alert('Please record your answer first.');
      return;
    }

    setIsProcessing(true);

    try {
      // Send transcript for evaluation
      const response = await axios.post(`http://localhost:5000/api/interview/${token}/evaluate-speech`, {
        question_id: currentQuestion.id,
        question_text: currentQuestion.question,
        transcript: transcript.trim(),
        recording_duration: recordingTime,
        question_type: 'speech'
      });

      const evalResult = response.data.evaluation || {
        overall_score: 75,
        feedback: 'Answer recorded successfully.',
        clarity_score: 80,
        content_score: 75,
        communication_score: 70
      };

      setEvaluation(evalResult);

      // Save response
      setResponses(prev => ({
        ...prev,
        [`speech_${currentQuestion.id}`]: {
          question: currentQuestion.question,
          transcript: transcript.trim(),
          duration: recordingTime,
          evaluation: evalResult,
          timestamp: new Date().toISOString()
        }
      }));

    } catch (error) {
      console.error('Error submitting speech answer:', error);
      // Still save the response even if evaluation fails
      setResponses(prev => ({
        ...prev,
        [`speech_${currentQuestion.id}`]: {
          question: currentQuestion.question,
          transcript: transcript.trim(),
          duration: recordingTime,
          timestamp: new Date().toISOString()
        }
      }));
    } finally {
      setIsProcessing(false);
    }
  };

  const nextQuestion = () => {
    if (currentQuestionIndex < totalQuestions - 1) {
      setCurrentQuestionIndex(prev => prev + 1);
      setTranscript('');
      setRecordingTime(0);
      setAudioBlob(null);
      setEvaluation(null);
    }
  };

  const previousQuestion = () => {
    if (currentQuestionIndex > 0) {
      setCurrentQuestionIndex(prev => prev - 1);
      // Load previous response if exists
      const prevResponse = responses[`speech_${questions[currentQuestionIndex - 1].id}`];
      if (prevResponse) {
        setTranscript(prevResponse.transcript || '');
        setEvaluation(prevResponse.evaluation || null);
      } else {
        setTranscript('');
        setEvaluation(null);
      }
      setRecordingTime(0);
      setAudioBlob(null);
    }
  };

  const completePhase = () => {
    onComplete(responses);
  };

  const formatRecordingTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  if (!speechSupported) {
    return (
      <div className="speech-test-container">
        <div className="speech-error">
          <h2>‚ö†Ô∏è Speech Recognition Not Supported</h2>
          <p>Your browser doesn't support speech recognition or microphone access was denied.</p>
          <p>Please use Chrome, Edge, or Safari and allow microphone access.</p>
          <button onClick={completePhase} className="btn-primary">
            Skip to Next Phase
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="speech-test-container">
      {/* Timer Bar */}
      <div className="timer-bar">
        <span>Phase: Speech Test</span>
        <span>Time Remaining: {formatTime(timeRemaining)}</span>
      </div>

      <div className="speech-content">
        {/* Sidebar with question navigator */}
        <div className="sidebar-questions">
          <h3>üé§ Speech Questions</h3>
          <div className="question-list">
            {questions.map((q, index) => {
              const isCurrentQuestion = index === currentQuestionIndex;
              const hasAnswer = !!responses[`speech_${q.id}`];

              return (
                <button
                  key={q.id}
                  className={`question-btn ${isCurrentQuestion ? 'active' : ''} ${hasAnswer ? 'answered' : ''}`}
                  onClick={() => {
                    setCurrentQuestionIndex(index);
                    const resp = responses[`speech_${q.id}`];
                    if (resp) {
                      setTranscript(resp.transcript || '');
                      setEvaluation(resp.evaluation || null);
                    } else {
                      setTranscript('');
                      setEvaluation(null);
                    }
                    setRecordingTime(0);
                    setAudioBlob(null);
                    setIsRecording(false);
                  }}
                  title={hasAnswer ? '‚úì Answered' : 'Not answered'}
                >
                  <span className="question-number">{index + 1}</span>
                  {hasAnswer && <span className="checkmark">‚úì</span>}
                </button>
              );
            })}
          </div>
        </div>

        {/* Main content */}
        <div className="question-content">
          <div className="question-header">
            <h2>üé§ Speech Question {currentQuestionIndex + 1} of {totalQuestions}</h2>
            <span className={`difficulty-badge ${currentQuestion?.difficulty?.toLowerCase()}`}>
              {currentQuestion?.difficulty}
            </span>
          </div>

          <div className="question-text">
            <p>{currentQuestion?.question}</p>
          </div>

          {/* Recording Area */}
          <div className="recording-area">
            <div className="recording-controls">
              {!isRecording ? (
                <button 
                  onClick={startRecording} 
                  className="record-btn start"
                  disabled={isProcessing}
                >
                  <span className="mic-icon">üéôÔ∏è</span>
                  Start Recording
                </button>
              ) : (
                <button 
                  onClick={stopRecording} 
                  className="record-btn stop"
                >
                  <span className="stop-icon">‚èπÔ∏è</span>
                  Stop Recording
                </button>
              )}

              {isRecording && (
                <div className="recording-indicator">
                  <span className="recording-dot"></span>
                  Recording: {formatRecordingTime(recordingTime)}
                </div>
              )}
            </div>

            {/* Transcript Display */}
            <div className="transcript-area">
              <h4>üìù Your Response (Live Transcription):</h4>
              <div className="transcript-box">
                {transcript || (
                  <span className="placeholder">
                    {isRecording 
                      ? 'Listening... Speak clearly into your microphone.'
                      : 'Click "Start Recording" and speak your answer.'}
                  </span>
                )}
              </div>
            </div>

            {/* Submit Button */}
            {transcript && !isRecording && (
              <div className="submit-area">
                <button 
                  onClick={submitAnswer} 
                  className="btn-primary submit-speech"
                  disabled={isProcessing}
                >
                  {isProcessing ? '‚è≥ Evaluating...' : '‚úÖ Submit Answer'}
                </button>
                <button 
                  onClick={startRecording} 
                  className="btn-secondary re-record"
                >
                  üîÑ Re-record
                </button>
              </div>
            )}

            {/* AI Evaluation Feedback */}
            {evaluation && (
              <div className="evaluation-feedback">
                <div className="feedback-header">
                  <span>ü§ñ AI Evaluation</span>
                  <span className="feedback-score">Score: {evaluation.overall_score}%</span>
                </div>
                <div className="feedback-details">
                  {evaluation.clarity_score && (
                    <div className="score-item">
                      <span>Clarity:</span>
                      <div className="score-bar">
                        <div className="score-fill" style={{width: `${evaluation.clarity_score}%`}}></div>
                      </div>
                      <span>{evaluation.clarity_score}%</span>
                    </div>
                  )}
                  {evaluation.content_score && (
                    <div className="score-item">
                      <span>Content:</span>
                      <div className="score-bar">
                        <div className="score-fill" style={{width: `${evaluation.content_score}%`}}></div>
                      </div>
                      <span>{evaluation.content_score}%</span>
                    </div>
                  )}
                  {evaluation.communication_score && (
                    <div className="score-item">
                      <span>Communication:</span>
                      <div className="score-bar">
                        <div className="score-fill" style={{width: `${evaluation.communication_score}%`}}></div>
                      </div>
                      <span>{evaluation.communication_score}%</span>
                    </div>
                  )}
                </div>
                <p className="feedback-text">{evaluation.feedback}</p>
              </div>
            )}
          </div>

          {/* Navigation */}
          <div className="navigation-buttons">
            <button
              onClick={previousQuestion}
              disabled={currentQuestionIndex === 0}
              className="btn-secondary"
            >
              ‚Üê Previous
            </button>

            {currentQuestionIndex === totalQuestions - 1 ? (
              <button onClick={completePhase} className="btn-primary">
                Complete Speech Test ‚Üí
              </button>
            ) : (
              <button onClick={nextQuestion} className="btn-primary">
                Next Question ‚Üí
              </button>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default SpeechTest;
