import React, { useState } from 'react';
import axios from 'axios';
import './JobCreation.css';

const JobCreation = ({ onBack }) => {
  const [formData, setFormData] = useState({
    title: '',
    department: '',
    location: '',
    experience_required: '',
    description: '',
    must_have_skills: '',
    good_to_have_skills: '',
  });

  const [submitting, setSubmitting] = useState(false);
  const [success, setSuccess] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitting(true);

    const mustHaveSkills = formData.must_have_skills
      .split(',')
      .map((s) => s.trim())
      .filter(Boolean);

    const goodToHaveSkills = formData.good_to_have_skills
      .split(',')
      .map((s) => s.trim())
      .filter(Boolean);

    const jobData = {
      ...formData,
      requirements: {
        must_have: mustHaveSkills,
        good_to_have: goodToHaveSkills,
      },
    };

    try {
      await axios.post('http://localhost:5000/api/jobs', jobData);
      setSuccess(true);
      setTimeout(() => onBack(), 2000);
    } catch (error) {
      console.error('Error creating job:', error);
      alert('Failed to create job');
    } finally {
      setSubmitting(false);
    }
  };

  if (success) {
    return (
      <div className="success-message">
        <h2 className="success-title">
          ✅ Job Created Successfully!
        </h2>
        <p>Redirecting to dashboard...</p>
      </div>
    );
  }

  return (
    <div className="job-creation-container">
      <button
        onClick={onBack}
        className="back-button"
      >
        ← Back
      </button>

      <div className="job-creation-card">
        <h2 className="job-creation-title">
          Create New Job Opening
        </h2>

        <form onSubmit={handleSubmit} className="job-form">
          <div className="form-group">
            <label className="form-label">
              Job Title <span className="required">*</span>
            </label>
            <input
              type="text"
              name="title"
              value={formData.title}
              onChange={handleChange}
              placeholder="e.g., Senior Python Developer"
              className="form-input"
              required
            />
          </div>

          <div className="form-row">
            <div className="form-group">
              <label className="form-label">
                Department <span className="required">*</span>
              </label>
              <input
                type="text"
                name="department"
                value={formData.department}
                onChange={handleChange}
                placeholder="e.g., Engineering"
                className="form-input"
                required
              />
            </div>

            <div className="form-group">
              <label className="form-label">
                Location <span className="required">*</span>
              </label>
              <input
                type="text"
                name="location"
                value={formData.location}
                onChange={handleChange}
                placeholder="e.g., Bangalore / Hybrid"
                className="form-input"
                required
              />
            </div>
          </div>

          <div className="form-group">
            <label className="form-label">
              Experience Required <span className="required">*</span>
            </label>
            <input
              type="text"
              name="experience_required"
              value={formData.experience_required}
              onChange={handleChange}
              placeholder="e.g., 5–7 years"
              className="form-input"
              required
            />
          </div>

          <div className="form-group">
            <label className="form-label">
              Job Description <span className="required">*</span>
            </label>
            <textarea
              name="description"
              value={formData.description}
              onChange={handleChange}
              rows="6"
              placeholder="Describe the role, responsibilities, and expectations..."
              className="form-textarea"
              required
            />
          </div>

          <div className="form-group">
            <label className="form-label">
              Must-Have Skills <span className="required">*</span> (comma-separated)
            </label>
            <input
              type="text"
              name="must_have_skills"
              value={formData.must_have_skills}
              onChange={handleChange}
              placeholder="python, sql, aws"
              className="form-input"
              required
            />
          </div>

          <div className="form-group">
            <label className="form-label">
              Good-to-Have Skills (comma-separated)
            </label>
            <input
              type="text"
              name="good_to_have_skills"
              value={formData.good_to_have_skills}
              onChange={handleChange}
              placeholder="docker, kubernetes, fastapi"
              className="form-input"
            />
          </div>

          <button
            type="submit"
            disabled={submitting}
            className="btn-submit"
            style={{ width: '100%' }}
          >
            {submitting ? 'Creating...' : 'Create Job & Publish'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default JobCreation;
