import React, { useState } from 'react';

function InterviewScheduler({ onBack }) {
  const [formData, setFormData] = useState({
    candidateId: '',
    jobId: '',
    interviewDate: '',
    interviewTime: '',
    interviewType: 'phone',
    interviewer: '',
    notes: ''
  });
  const [submitted, setSubmitted] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Scheduling interview:', formData);
    setSubmitted(true);
    setTimeout(() => {
      setSubmitted(false);
      if (onBack) onBack();
    }, 2000);
  };

  if (submitted) {
    return (
      <div className="bg-green-50 border border-green-200 rounded-lg p-8 text-center max-w-2xl mx-auto">
        <h2 className="text-2xl font-bold text-green-800 mb-2">✅ Interview Scheduled!</h2>
        <p className="text-green-700">Redirecting...</p>
      </div>
    );
  }

  return (
    <div className="max-w-2xl mx-auto">
      {onBack && (
        <button 
          onClick={onBack}
          className="mb-6 px-4 py-2 bg-gray-600 text-white rounded-lg hover:bg-gray-700 transition-colors flex items-center gap-2"
        >
          ← Back
        </button>
      )}
      
      <div className="bg-white rounded-xl shadow-lg p-8">
        <h1 className="text-3xl font-bold text-gray-800 mb-8">Schedule Interview</h1>
        <form onSubmit={handleSubmit} className="space-y-6">
          <div>
            <label htmlFor="candidateId" className="block text-sm font-semibold text-gray-700 mb-2">Candidate</label>
            <select
              id="candidateId"
              name="candidateId"
              value={formData.candidateId}
              onChange={handleChange}
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:border-blue-500 focus:ring-2 focus:ring-blue-200 transition-all bg-white"
              required
            >
              <option value="">Select Candidate</option>
              <option value="C001">John Doe</option>
              <option value="C002">Jane Smith</option>
            </select>
          </div>

          <div>
            <label htmlFor="jobId" className="block text-sm font-semibold text-gray-700 mb-2">Job Position</label>
            <select
              id="jobId"
              name="jobId"
              value={formData.jobId}
              onChange={handleChange}
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:border-blue-500 focus:ring-2 focus:ring-blue-200 transition-all bg-white"
              required
            >
              <option value="">Select Job</option>
              <option value="J001">Software Engineer</option>
              <option value="J002">Backend Engineer</option>
            </select>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <label htmlFor="interviewDate" className="block text-sm font-semibold text-gray-700 mb-2">Interview Date</label>
              <input
                type="date"
                id="interviewDate"
                name="interviewDate"
                value={formData.interviewDate}
                onChange={handleChange}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:border-blue-500 focus:ring-2 focus:ring-blue-200 transition-all"
                required
              />
            </div>
            <div>
              <label htmlFor="interviewTime" className="block text-sm font-semibold text-gray-700 mb-2">Interview Time</label>
              <input
                type="time"
                id="interviewTime"
                name="interviewTime"
                value={formData.interviewTime}
                onChange={handleChange}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:border-blue-500 focus:ring-2 focus:ring-blue-200 transition-all"
                required
              />
            </div>
          </div>

          <div>
            <label htmlFor="interviewType" className="block text-sm font-semibold text-gray-700 mb-2">Interview Type</label>
            <select
              id="interviewType"
              name="interviewType"
              value={formData.interviewType}
              onChange={handleChange}
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:border-blue-500 focus:ring-2 focus:ring-blue-200 transition-all bg-white"
            >
              <option value="phone">Phone Screen</option>
              <option value="video">Video Call</option>
              <option value="in-person">In-Person</option>
              <option value="technical">Technical Interview</option>
            </select>
          </div>

          <div>
            <label htmlFor="interviewer" className="block text-sm font-semibold text-gray-700 mb-2">Interviewer</label>
            <input
              type="text"
              id="interviewer"
              name="interviewer"
              value={formData.interviewer}
              onChange={handleChange}
              placeholder="Interviewer name"
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:border-blue-500 focus:ring-2 focus:ring-blue-200 transition-all"
            />
          </div>

          <div>
            <label htmlFor="notes" className="block text-sm font-semibold text-gray-700 mb-2">Notes</label>
            <textarea
              id="notes"
              name="notes"
              value={formData.notes}
              onChange={handleChange}
              placeholder="Additional notes"
              rows="4"
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:border-blue-500 focus:ring-2 focus:ring-blue-200 transition-all resize-vertical"
            />
          </div>

          <button 
            type="submit" 
            className="w-full px-6 py-3 bg-blue-600 text-white font-semibold rounded-lg hover:bg-blue-700 transition-all transform hover:scale-105"
          >
            Schedule Interview
          </button>
        </form>
      </div>
    </div>
  );
}
export default InterviewScheduler;
