import React, { useState, useEffect } from 'react';

function OfferManagement({ onBack }) {
  const [offers, setOffers] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({
    candidateId: '',
    jobId: '',
    salaryOffered: '',
    startDate: '',
    benefits: '',
    notes: ''
  });

  useEffect(() => {
    // TODO: Fetch existing offers from backend
    fetchOffers();
  }, []);

  const fetchOffers = async () => {
    // Mock data
    setOffers([
      {
        id: 'O001',
        candidateName: 'John Doe',
        jobTitle: 'Software Engineer',
        salary: '$120,000',
        status: 'pending',
        createdDate: '2024-01-15'
      },
      {
        id: 'O002',
        candidateName: 'Jane Smith',
        jobTitle: 'Backend Engineer',
        salary: '$140,000',
        status: 'accepted',
        createdDate: '2024-01-10'
      }
    ]);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // TODO: Send offer creation request to backend
    console.log('Creating offer:', formData);
    setShowForm(false);
    setFormData({
      candidateId: '',
      jobId: '',
      salaryOffered: '',
      startDate: '',
      benefits: '',
      notes: ''
    });
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'accepted':
        return 'bg-green-100 text-green-800';
      case 'pending':
        return 'bg-yellow-100 text-yellow-800';
      case 'rejected':
        return 'bg-red-100 text-red-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  return (
    <div className="space-y-6">
      {onBack && (
        <button 
          onClick={onBack}
          className="px-4 py-2 bg-gray-600 text-white rounded-lg hover:bg-gray-700 transition-colors"
        >
          ‚Üê Back
        </button>
      )}

      <div className="bg-white rounded-xl shadow-lg p-8">
        <div className="flex justify-between items-center mb-8">
          <h1 className="text-3xl font-bold text-gray-800">Offer Management</h1>
          <button 
            className="px-6 py-3 bg-blue-600 text-white font-semibold rounded-lg hover:bg-blue-700 transition-all transform hover:scale-105"
            onClick={() => setShowForm(!showForm)}
          >
            {showForm ? 'Cancel' : 'Create New Offer'}
          </button>
        </div>

        {showForm && (
          <form onSubmit={handleSubmit} className="bg-gray-50 border-2 border-blue-200 rounded-lg p-6 mb-6 space-y-6">
            <div>
              <label htmlFor="candidateId" className="block text-sm font-semibold text-gray-700 mb-2">Candidate</label>
              <select
                id="candidateId"
                name="candidateId"
                value={formData.candidateId}
                onChange={handleChange}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:border-blue-500 focus:ring-2 focus:ring-blue-200 transition-all bg-white"
                required
              >
                <option value="">Select Candidate</option>
                <option value="C001">John Doe</option>
                <option value="C002">Jane Smith</option>
              </select>
            </div>

            <div>
              <label htmlFor="jobId" className="block text-sm font-semibold text-gray-700 mb-2">Job Position</label>
              <select
                id="jobId"
                name="jobId"
                value={formData.jobId}
                onChange={handleChange}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:border-blue-500 focus:ring-2 focus:ring-blue-200 transition-all bg-white"
              required
              >
                <option value="">Select Job</option>
                <option value="J001">Software Engineer</option>
                <option value="J002">Backend Engineer</option>
              </select>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <label htmlFor="salaryOffered" className="block text-sm font-semibold text-gray-700 mb-2">Salary Offered</label>
                <input
                  type="number"
                  id="salaryOffered"
                  name="salaryOffered"
                  value={formData.salaryOffered}
                  onChange={handleChange}
                  placeholder="e.g., 120000"
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:border-blue-500 focus:ring-2 focus:ring-blue-200 transition-all"
                  required
                />
              </div>

              <div>
                <label htmlFor="startDate" className="block text-sm font-semibold text-gray-700 mb-2">Start Date</label>
                <input
                  type="date"
                  id="startDate"
                  name="startDate"
                  value={formData.startDate}
                  onChange={handleChange}
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:border-blue-500 focus:ring-2 focus:ring-blue-200 transition-all"
                  required
                />
              </div>
            </div>

            <div>
              <label htmlFor="benefits" className="block text-sm font-semibold text-gray-700 mb-2">Benefits</label>
              <textarea
                id="benefits"
                name="benefits"
                value={formData.benefits}
                onChange={handleChange}
                placeholder="List benefits offered"
                rows="3"
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:border-blue-500 focus:ring-2 focus:ring-blue-200 transition-all resize-vertical"
              />
            </div>

            <div>
              <label htmlFor="notes" className="block text-sm font-semibold text-gray-700 mb-2">Notes</label>
              <textarea
                id="notes"
                name="notes"
                value={formData.notes}
                onChange={handleChange}
                placeholder="Additional notes"
                rows="3"
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:border-blue-500 focus:ring-2 focus:ring-blue-200 transition-all resize-vertical"
              />
            </div>

            <button 
              type="submit" 
              className="w-full px-6 py-3 bg-blue-600 text-white font-semibold rounded-lg hover:bg-blue-700 transition-all transform hover:scale-105"
            >
              Create Offer
            </button>
          </form>
        )}
      </div>

      <div className="bg-white rounded-xl shadow-lg overflow-hidden">
        <div className="overflow-x-auto">
          <table className="w-full">
            <thead>
              <tr className="bg-gray-100 border-b-2 border-gray-300">
                <th className="text-left py-4 px-6 font-semibold text-gray-700">Candidate</th>
                <th className="text-left py-4 px-6 font-semibold text-gray-700">Job Title</th>
                <th className="text-left py-4 px-6 font-semibold text-gray-700">Salary</th>
                <th className="text-left py-4 px-6 font-semibold text-gray-700">Status</th>
                <th className="text-left py-4 px-6 font-semibold text-gray-700">Created Date</th>
                <th className="text-left py-4 px-6 font-semibold text-gray-700">Actions</th>
              </tr>
            </thead>
            <tbody>
              {offers.map(offer => (
                <tr key={offer.id} className="border-b border-gray-200 hover:bg-gray-50 transition-colors">
                  <td className="py-4 px-6 text-gray-800 font-semibold">{offer.candidateName}</td>
                  <td className="py-4 px-6 text-gray-700">{offer.jobTitle}</td>
                  <td className="py-4 px-6 text-gray-700 font-semibold">{offer.salary}</td>
                  <td className="py-4 px-6">
                    <span className={`px-4 py-2 rounded-full text-sm font-semibold ${getStatusColor(offer.status)}`}>
                      {offer.status.charAt(0).toUpperCase() + offer.status.slice(1)}
                    </span>
                  </td>
                  <td className="py-4 px-6 text-gray-700">{offer.createdDate}</td>
                  <td className="py-4 px-6">
                    <button className="px-4 py-2 bg-blue-600 text-white text-sm rounded-lg hover:bg-blue-700 transition-colors mr-2">
                      View
                    </button>
                    <button className="px-4 py-2 bg-gray-600 text-white text-sm rounded-lg hover:bg-gray-700 transition-colors">
                      Edit
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default OfferManagement;
