import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import '../styles/InterviewSession.css';

const InterviewSession = ({ token }) => {
  // Main states
  const [interviewData, setInterviewData] = useState(null);
  const [currentPhase, setCurrentPhase] = useState('welcome');
  const [status, setStatus] = useState('loading');
  const [loading, setLoading] = useState(false);

  // Question management
  const [allQuestions, setAllQuestions] = useState({ technical: [], behavioral: [], coding: [] });
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [responses, setResponses] = useState({});

  // Coding challenge
  const [codingCode, setCodingCode] = useState('');
  const [selectedLanguage, setSelectedLanguage] = useState('python');
  const [compileOutput, setCompileOutput] = useState('');
  const [codeEvaluation, setCodeEvaluation] = useState(null);
  const [codeSubmitted, setCodeSubmitted] = useState(false);

  // Timer states
  const [timeRemaining, setTimeRemaining] = useState(0);
  const [phaseTimeRemaining, setPhaseTimeRemaining] = useState(0);
  const timerRef = useRef(null);
  const phaseTimerRef = useRef(null);

  // Score
  const [score, setScore] = useState(null);

  // NEW PHASE ORDER: Technical -> Coding -> Behavioral
  const phases = [
    { id: 'welcome', label: 'Welcome', duration: 0 },
    { id: 'instruction', label: 'Instructions', duration: 0 },
    { id: 'technical', label: 'Technical Questions (15)', duration: 30 * 60 },
    { id: 'coding', label: 'Coding Challenge', duration: 0 },
    { id: 'behavioral', label: 'Behavioral Questions (20)', duration: 40 * 60 },
    { id: 'completion', label: 'Completion', duration: 0 }
  ];

  const programmingLanguages = ['Python', 'JavaScript', 'Java', 'C++', 'Go', 'Rust'];

  useEffect(() => {
    fetchInterviewStatus();
  }, [token]);

  // Save answers to localStorage for persistence
  useEffect(() => {
    if (Object.keys(responses).length > 0) {
      localStorage.setItem(`interview_responses_${token}`, JSON.stringify(responses));
    }
  }, [responses, token]);

  useEffect(() => {
    if (phaseTimeRemaining > 0 && currentPhase !== 'welcome' && currentPhase !== 'instruction' && currentPhase !== 'completion') {
      const interval = setInterval(() => {
        setPhaseTimeRemaining(prev => {
          if (prev <= 1) {
            moveToNextPhase();
            return 0;
          }
          return prev - 1;
        });
      }, 1000);
      return () => clearInterval(interval);
    }
  }, [phaseTimeRemaining, currentPhase]);

  useEffect(() => {
    if (timeRemaining > 0) {
      const interval = setInterval(() => {
        setTimeRemaining(prev => {
          if (prev <= 1) {
            completeInterview();
            return 0;
          }
          return prev - 1;
        });
      }, 1000);
      return () => clearInterval(interval);
    }
  }, [timeRemaining]);

  const fetchInterviewStatus = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/interview/${token}/status`);
      setInterviewData(response.data);
      setStatus(response.data.status);
      if (response.data.status === 'completed') {
        setScore(response.data.interview_score);
      }
      // Load saved responses from localStorage
      const savedResponses = localStorage.getItem(`interview_responses_${token}`);
      if (savedResponses) {
        setResponses(JSON.parse(savedResponses));
      }
    } catch (error) {
      console.error('Error fetching interview:', error);
      setStatus('error');
    }
  };

  const startInterview = async () => {
    try {
      setLoading(true);
      const response = await axios.post(`http://localhost:5000/api/interview/${token}/start`);
      setInterviewData(response.data);
      setStatus('in_progress');

      // Load questions (with timeout to use fallback if API is slow)
      loadQuestionsWithTimeout(response.data);

      setTimeRemaining(130 * 60); // 130 minutes total
      setCurrentPhase('instruction');
    } catch (error) {
      console.error('Error starting interview:', error);
      setStatus('error');
    } finally {
      setLoading(false);
    }
  };

  const loadQuestionsWithTimeout = async (interviewData) => {
    try {
      const timeout = new Promise((_, reject) =>
        setTimeout(() => reject(new Error('API timeout')), 60000) // Increased to 60s for AI generation
      );

      const loadPromise = axios.post(
        `http://localhost:5000/api/interview/${token}/start`,
        {}
      );

      const result = await Promise.race([loadPromise, timeout]);

      if (result.data.questions && Array.isArray(result.data.questions)) {
        const technical = result.data.questions.filter(q => q.type === 'technical');
        const behavioral = result.data.questions.filter(q => q.type === 'behavioral');
        const coding = result.data.questions.filter(q => q.type === 'coding');

        setAllQuestions({
          technical: technical || [],
          behavioral: behavioral || [],
          coding: coding || []
        });
      }
    } catch (error) {
      console.warn('API failed or timed out, using fallback questions');
      loadFallbackQuestions();
    }
  };

  const loadFallbackQuestions = () => {
    const fallbackQuestions = {
      technical: [
        { id: 1, question: 'What is the difference between var, let, and const in JavaScript?', difficulty: 'Easy', type: 'technical' },
        { id: 2, question: 'Explain the concept of closures in JavaScript.', difficulty: 'Easy', type: 'technical' },
        { id: 3, question: 'What is event delegation?', difficulty: 'Easy', type: 'technical' },
        { id: 4, question: 'What is the difference between == and === in JavaScript?', difficulty: 'Easy', type: 'technical' },
        { id: 5, question: 'How does prototypal inheritance work in JavaScript?', difficulty: 'Easy', type: 'technical' },
        { id: 6, question: 'What are JavaScript promises and how do they work?', difficulty: 'Easy', type: 'technical' },
        { id: 7, question: 'Explain async/await and how it improves promise handling.', difficulty: 'Medium', type: 'technical' },
        { id: 8, question: 'What is the event loop in JavaScript?', difficulty: 'Medium', type: 'technical' },
        { id: 9, question: 'Explain the concept of hoisting in JavaScript.', difficulty: 'Medium', type: 'technical' },
        { id: 10, question: 'What are decorators and how are they used?', difficulty: 'Medium', type: 'technical' },
        { id: 11, question: 'Explain the concept of memoization.', difficulty: 'Medium', type: 'technical' },
        { id: 12, question: 'What are the differences between SQL and NoSQL databases?', difficulty: 'Hard', type: 'technical' },
        { id: 13, question: 'How would you optimize a slow database query?', difficulty: 'Hard', type: 'technical' },
        { id: 14, question: 'Explain the differences between REST and GraphQL.', difficulty: 'Hard', type: 'technical' },
        { id: 15, question: 'What are the key principles of SOLID design?', difficulty: 'Hard', type: 'technical' },
      ],
      behavioral: [
        { id: 1, question: 'Tell us about a time when you had to work with a difficult team member.', difficulty: 'Easy', type: 'behavioral' },
        { id: 2, question: 'Describe a situation where you had to meet a tight deadline.', difficulty: 'Easy', type: 'behavioral' },
        { id: 3, question: 'Give an example of when you took initiative.', difficulty: 'Easy', type: 'behavioral' },
        { id: 4, question: 'How do you handle feedback and criticism?', difficulty: 'Easy', type: 'behavioral' },
        { id: 5, question: 'Tell us about a time you made a mistake at work.', difficulty: 'Easy', type: 'behavioral' },
        { id: 6, question: 'Describe your greatest professional achievement.', difficulty: 'Easy', type: 'behavioral' },
        { id: 7, question: 'How do you prioritize multiple tasks with competing deadlines?', difficulty: 'Easy', type: 'behavioral' },
        { id: 8, question: 'Tell us about a time you had to learn something new quickly.', difficulty: 'Easy', type: 'behavioral' },
        { id: 9, question: 'Give an example of when you showed leadership.', difficulty: 'Medium', type: 'behavioral' },
        { id: 10, question: 'Describe a time when you had to solve a complex problem.', difficulty: 'Medium', type: 'behavioral' },
        { id: 11, question: 'Tell us about a conflict you resolved at work.', difficulty: 'Medium', type: 'behavioral' },
        { id: 12, question: 'How do you stay motivated when working on repetitive tasks?', difficulty: 'Medium', type: 'behavioral' },
        { id: 13, question: 'Give an example of how you improved a process or system.', difficulty: 'Medium', type: 'behavioral' },
        { id: 14, question: 'Tell us about a time you had to present to senior management.', difficulty: 'Medium', type: 'behavioral' },
        { id: 15, question: 'Describe a situation where you had to adapt to a major change.', difficulty: 'Medium', type: 'behavioral' },
        { id: 16, question: 'Give an example of how you mentor or support junior colleagues.', difficulty: 'Hard', type: 'behavioral' },
        { id: 17, question: 'Tell us about a time when you disagreed with a decision made by leadership.', difficulty: 'Hard', type: 'behavioral' },
        { id: 18, question: 'Describe a time you had to make an ethical decision at work.', difficulty: 'Hard', type: 'behavioral' },
        { id: 19, question: 'How do you handle working with remote or distributed teams?', difficulty: 'Hard', type: 'behavioral' },
        { id: 20, question: 'Tell us about your career vision and how this role fits into it.', difficulty: 'Hard', type: 'behavioral' },
      ],
      coding: [
        { id: 1, question: 'Write a function that reverses a string.', difficulty: 'Easy', type: 'coding' },
      ]
    };

    setAllQuestions(fallbackQuestions);
  };

  const getCurrentPhaseQuestions = () => {
    if (currentPhase === 'technical') return allQuestions.technical;
    if (currentPhase === 'behavioral') return allQuestions.behavioral;
    if (currentPhase === 'coding') return allQuestions.coding;
    return [];
  };

  const getCurrentQuestion = () => {
    const questions = getCurrentPhaseQuestions();
    return questions[currentQuestionIndex] || null;
  };

  // Save answer when leaving a question (NEW: doesn't clear responses)
  const saveCurrentAnswer = async (answer) => {
    const question = getCurrentQuestion();
    if (!question) return;

    const key = `${currentPhase}_${question.id}`;

    // Update responses state without clearing
    setResponses(prev => ({
      ...prev,
      [key]: {
        question: question.question,
        answer: answer,
        difficulty: question.difficulty,
        timestamp: new Date().toISOString()
      }
    }));

    // Only submit to backend if not on coding phase
    if (currentPhase !== 'coding') {
      try {
        const response = await axios.post(`http://localhost:5000/api/interview/${token}/submit-response`, {
          question_id: question.id,
          response: answer,
          phase: currentPhase,
          question_text: question.question,
          question_type: currentPhase
        });

        // Update with AI evaluation if returned
        if (response.data.evaluation) {
          setResponses(prev => ({
            ...prev,
            [key]: {
              ...prev[key],
              evaluation: response.data.evaluation
            }
          }));
        }
      } catch (error) {
        console.error('Error submitting response:', error);
      }
    }
  };

  const handleQuestionNavigation = (index) => {
    // Save current answer before switching
    const question = getCurrentQuestion();
    if (question && currentPhase !== 'coding') {
      const textareaElement = document.querySelector('textarea[name="answer"]');
      if (textareaElement && textareaElement.value) {
        saveCurrentAnswer(textareaElement.value);
      }
    }

    // Navigate to new question
    setCurrentQuestionIndex(index);
    setCodeEvaluation(null);
    setCompileOutput('');
  };

  const moveToNextQuestion = () => {
    const questions = getCurrentPhaseQuestions();
    const question = getCurrentQuestion();

    if (currentPhase !== 'coding' && question) {
      const textareaElement = document.querySelector('textarea[name="answer"]');
      if (textareaElement && textareaElement.value) {
        saveCurrentAnswer(textareaElement.value);
      }
    }

    if (currentQuestionIndex < questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
      setCodeEvaluation(null);
      setCompileOutput('');
    }
  };

  const moveToNextPhase = () => {
    const currentIndex = phases.findIndex(p => p.id === currentPhase);
    if (currentIndex < phases.length - 1) {
      const nextPhase = phases[currentIndex + 1];
      setCurrentPhase(nextPhase.id);
      setCurrentQuestionIndex(0);
      setCodingCode('');
      setSelectedLanguage('python');
      setCodeEvaluation(null);
      setCompileOutput('');
      setCodeSubmitted(false);

      if (nextPhase.duration > 0) {
        setPhaseTimeRemaining(nextPhase.duration);
      }
    }
  };

  const compileAndRunCode = async () => {
    try {
      setLoading(true);
      setCompileOutput('Compiling and testing...');

      const response = await axios.post(
        `http://localhost:5000/api/interview/${token}/evaluate-code-strict`,
        {
          code: codingCode,
          language: selectedLanguage.toLowerCase()
        }
      );

      const evaluation = response.data;
      setCodeEvaluation(evaluation);

      if (evaluation.verdict === 'PASS') {
        setCompileOutput('âœ… All tests passed! You can now submit.');
      } else if (evaluation.errors) {
        setCompileOutput(`âŒ Compilation/Runtime Error:\n${evaluation.errors}`);
      } else if (evaluation.test_results) {
        const failedTests = evaluation.test_results.filter(t => !t.passed);
        setCompileOutput(`âš ï¸ ${failedTests.length} test(s) failed. You can still submit if you want.`);
      } else {
        setCompileOutput(`Result: ${evaluation.verdict}`);
      }
    } catch (error) {
      console.error('Error compiling code:', error);
      setCompileOutput('âŒ Error compiling code. Please check your syntax.');
    } finally {
      setLoading(false);
    }
  };

  const submitCode = async () => {
    try {
      setLoading(true);

      // IMPORTANT: Allow submission even if tests failed
      const response = await axios.post(
        `http://localhost:5000/api/interview/${token}/submit-response`,
        {
          question_id: 1,
          response: codingCode,
          phase: 'coding',
          language: selectedLanguage.toLowerCase(),
          evaluation: codeEvaluation
        }
      );

      setCodeSubmitted(true);

      // Save to responses
      setResponses(prev => ({
        ...prev,
        'coding_1': {
          question: 'Write code to solve the problem',
          answer: codingCode,
          language: selectedLanguage,
          evaluation: codeEvaluation,
          timestamp: new Date().toISOString()
        }
      }));

      // Auto move to next phase after 2 seconds
      setTimeout(() => {
        moveToNextPhase();
      }, 2000);
    } catch (error) {
      console.error('Error submitting code:', error);
      alert('Error submitting code. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const completeInterview = async () => {
    try {
      const response = await axios.post(`http://localhost:5000/api/interview/${token}/complete`);
      setStatus('completed');
      setScore(response.data.interview_score);
      setCurrentPhase('completion');
    } catch (error) {
      console.error('Error completing interview:', error);
    }
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  // ============ RENDER FUNCTIONS ============

  if (status === 'loading' || !interviewData) {
    return <div className="loading">Loading interview...</div>;
  }

  if (status === 'error') {
    return <div className="error">Error loading interview. Please try again.</div>;
  }

  if (currentPhase === 'welcome') {
    return (
      <div className="interview-container">
        <div className="welcome-screen">
          <h1>Welcome to Your Interview</h1>
          <p>Candidate: {interviewData.candidate_name}</p>
          <p>Position: {interviewData.job_title}</p>
          <p>This interview will consist of:</p>
          <ul>
            <li>ðŸ“ Technical Questions (15 questions, 30 minutes)</li>
            <li>ðŸ’» Coding Challenge (solve a programming problem)</li>
            <li>ðŸŽ¯ Behavioral Questions (20 questions, 40 minutes)</li>
          </ul>
          <button onClick={startInterview} disabled={loading} className="btn-primary">
            {loading ? 'Starting...' : 'Start Interview'}
          </button>
        </div>
      </div>
    );
  }

  if (currentPhase === 'instruction') {
    return (
      <div className="interview-container">
        <div className="instruction-screen">
          <h1>Interview Instructions</h1>
          <div className="instructions">
            <h3>âœ… Technical Questions (30 minutes)</h3>
            <p>Answer 15 technical questions. You can navigate between questions freely.</p>

            <h3>ðŸ’» Coding Challenge</h3>
            <p>Write code to solve a programming problem. Compile & test before submitting.</p>
            <p>You can submit even if some tests fail.</p>

            <h3>ðŸŽ¯ Behavioral Questions (40 minutes)</h3>
            <p>Answer 20 behavioral questions about your experience and approach.</p>

            <h3>ðŸ“‹ General Tips</h3>
            <ul>
              <li>You can switch between questions freely</li>
              <li>Your answers are saved automatically</li>
              <li>Time remaining is shown at the top</li>
              <li>Answer as thoroughly as possible</li>
            </ul>
          </div>
          <button onClick={moveToNextPhase} className="btn-primary">Begin Technical Questions</button>
        </div>
      </div>
    );
  }

  if (currentPhase === 'completion') {
    return (
      <div className="interview-container">
        <div className="completion-screen">
          <h1>Interview Complete!</h1>
          <p>Thank you for your time.</p>
          {score !== null && (
            <div className="score-display">
              <h2>Overall Score: {score.toFixed(1)}%</h2>
            </div>
          )}
          <button onClick={() => window.location.href = '/'} className="btn-primary">
            Return to Dashboard
          </button>
        </div>
      </div>
    );
  }

  if (currentPhase === 'technical' || currentPhase === 'behavioral') {
    const questions = getCurrentPhaseQuestions();
    const question = getCurrentQuestion();
    const phaseLabel = currentPhase === 'technical' ? 'Technical' : 'Behavioral';
    const totalQuestions = questions.length;
    const savedAnswer = responses[`${currentPhase}_${question?.id}`]?.answer || '';
    const isAnswered = savedAnswer.length > 0;

    return (
      <div className="interview-container">
        <div className="timer-bar">
          <span>Phase Timer: {formatTime(phaseTimeRemaining)}</span>
          <span>Total Time: {formatTime(timeRemaining)}</span>
        </div>

        <div className="content-area">
          {/* SIDEBAR with Question Navigator */}
          <div className="sidebar-questions">
            <h3>{phaseLabel} Questions</h3>
            <div className="question-list">
              {questions.map((q, index) => {
                const answerKey = `${currentPhase}_${q.id}`;
                const isCurrentQuestion = index === currentQuestionIndex;
                const hasAnswer = !!responses[answerKey];

                return (
                  <button
                    key={q.id}
                    className={`question-btn ${isCurrentQuestion ? 'active' : ''} ${hasAnswer ? 'answered' : ''}`}
                    onClick={() => handleQuestionNavigation(index)}
                    title={hasAnswer ? 'âœ“ Answered' : 'Not answered'}
                  >
                    <span className="question-number">{index + 1}</span>
                    {hasAnswer && <span className="checkmark">âœ“</span>}
                  </button>
                );
              })}
            </div>
          </div>

          {/* MAIN CONTENT */}
          <div className="question-content">
            {question ? (
              <>
                <div className="question-header">
                  <h2>{phaseLabel} Question {currentQuestionIndex + 1} of {totalQuestions}</h2>
                  <span className={`difficulty-badge ${question.difficulty?.toLowerCase()}`}>
                    {question.difficulty}
                  </span>
                </div>

                <div className="question-text">
                  <p>{question.question}</p>
                </div>

                <div className="answer-area">
                  <textarea
                    name="answer"
                    placeholder="Type your answer here..."
                    defaultValue={savedAnswer}
                    onBlur={(e) => {
                      // Save when user finishes typing
                      saveCurrentAnswer(e.target.value);
                    }}
                    onChange={(e) => {
                      // Update local state for UI responsiveness
                      setResponses(prev => ({
                        ...prev,
                        [`${currentPhase}_${question.id}`]: {
                          ...prev[`${currentPhase}_${question.id}`],
                          answer: e.target.value,
                          timestamp: new Date().toISOString()
                        }
                      }));
                    }}
                  />

                  {responses[`${currentPhase}_${question.id}`]?.evaluation && (
                    <div className="ai-feedback-toast">
                      <div className="feedback-header">
                        <span>ðŸ¤– AI Feedback</span>
                        <span className="feedback-score">Score: {responses[`${currentPhase}_${question.id}`].evaluation.overall_score}%</span>
                      </div>
                      <p>{responses[`${currentPhase}_${question.id}`].evaluation.feedback}</p>
                    </div>
                  )}
                </div>

                <div className="navigation-buttons">
                  <button
                    onClick={() => {
                      if (currentQuestionIndex > 0) {
                        handleQuestionNavigation(currentQuestionIndex - 1);
                      }
                    }}
                    disabled={currentQuestionIndex === 0}
                    className="btn-secondary"
                  >
                    â† Previous
                  </button>

                  {currentQuestionIndex === totalQuestions - 1 ? (
                    <button onClick={moveToNextPhase} className="btn-primary">
                      Complete {phaseLabel} Phase â†’
                    </button>
                  ) : (
                    <button onClick={moveToNextQuestion} className="btn-primary">
                      Next â†’
                    </button>
                  )}
                </div>
              </>
            ) : (
              <div>No questions available</div>
            )}
          </div>
        </div>
      </div>
    );
  }

  if (currentPhase === 'coding') {
    const question = getCurrentQuestion();
    return (
      <div className="interview-container">
        <div className="timer-bar">
          <span>Total Time: {formatTime(timeRemaining)}</span>
        </div>

        <div className="coding-content">
          <div className="coding-header">
            <h2>Coding Challenge</h2>
            <div className="language-selector">
              <label>Programming Language:</label>
              <select
                value={selectedLanguage}
                onChange={(e) => setSelectedLanguage(e.target.value)}
                disabled={codeSubmitted}
              >
                {programmingLanguages.map(lang => (
                  <option key={lang} value={lang}>{lang}</option>
                ))}
              </select>
            </div>
          </div>

          <div className="coding-problem">
            <h3>{question?.title || 'Coding Challenge'}</h3>
            <div className="problem-metadata">
              <span className={`difficulty-badge ${question?.difficulty?.toLowerCase()}`}>
                {question?.difficulty || 'Medium'}
              </span>
            </div>

            <div className="problem-description">
              <p>{question?.question || question?.description || 'Solve the given coding problem.'}</p>
            </div>

            {question?.examples && (
              <div className="problem-examples">
                <strong>Examples:</strong>
                <pre>{JSON.stringify(question.examples, null, 2)}</pre>
              </div>
            )}

            {question?.constraints && (
              <div className="problem-constraints">
                <strong>Constraints:</strong>
                <ul>
                  {(Array.isArray(question.constraints) ? question.constraints : []).map((c, i) => (
                    <li key={i}>{c}</li>
                  ))}
                </ul>
              </div>
            )}
          </div>

          <div className="code-editor-area">
            <textarea
              className="code-editor"
              value={codingCode}
              onChange={(e) => setCodingCode(e.target.value)}
              placeholder={`// Write your ${selectedLanguage} code here\nfunction reverseString(str) {\n  // Your code\n}\n`}
              disabled={codeSubmitted}
            />
          </div>

          {compileOutput && (
            <div className={`compile-output ${codeEvaluation?.verdict === 'PASS' ? 'success' : 'error'}`}>
              <pre>{compileOutput}</pre>
            </div>
          )}

          {codeEvaluation && (
            <div className="evaluation-results">
              <h4>Test Results</h4>
              {codeEvaluation.test_results?.map((test, idx) => (
                <div key={idx} className={`test-result ${test.passed ? 'passed' : 'failed'}`}>
                  <span>{test.passed ? 'âœ“' : 'âœ—'} {test.name || `Test ${idx + 1}`}</span>
                </div>
              ))}
            </div>
          )}

          <div className="coding-buttons">
            <button
              onClick={compileAndRunCode}
              disabled={loading || codeSubmitted || !codingCode.trim()}
              className="btn-secondary"
            >
              {loading ? 'Compiling...' : 'â–¶ Compile & Run'}
            </button>
            <button
              onClick={submitCode}
              disabled={loading || codeSubmitted}
              className="btn-primary"
            >
              {codeSubmitted ? 'âœ“ Submitted' : 'ðŸ“¤ Submit Code'}
            </button>
          </div>

          {codeSubmitted && (
            <div className="submission-message">
              <p>âœ“ Code submitted successfully! Moving to next phase...</p>
            </div>
          )}
        </div>
      </div>
    );
  }

  return <div>Unknown phase: {currentPhase}</div>;
};

export default InterviewSession;
