import React, { useState } from 'react';
import './App.css';
import Dashboard from './components/Dashboard';
import JobCreation from './components/JobCreation';
import CandidateList from './components/CandidateList';
import InterviewScheduler from './components/InterviewScheduler';
import OfferManagement from './components/OfferManagement';

function App() {
  const [currentView, setCurrentView] = useState('dashboard');
  const [selectedJob, setSelectedJob] = useState(null);

  const renderView = () => {
    switch(currentView) {
      case 'dashboard':
        return <Dashboard onNavigate={setCurrentView} />;
      case 'create-job':
        return <JobCreation onBack={() => setCurrentView('dashboard')} />;
      case 'candidates':
        return <CandidateList 
                 jobId={selectedJob} 
                 onSchedule={() => setCurrentView('schedule')} 
               />;
      case 'schedule':
        return <InterviewScheduler onBack={() => setCurrentView('candidates')} />;
      case 'offers':
        return <OfferManagement onBack={() => setCurrentView('dashboard')} />;
      default:
        return <Dashboard onNavigate={setCurrentView} />;
    }
  };

  return (
    <div className="min-h-screen flex flex-col bg-gray-50">
      <nav className="bg-gradient-to-r from-blue-600 to-purple-600 shadow-lg">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            <h1 className="text-2xl font-bold text-white">ðŸŽ¯ GCC Hiring System</h1>
            <div className="flex gap-3 flex-wrap">
              <button 
                onClick={() => setCurrentView('dashboard')}
                className="px-4 py-2 bg-white bg-opacity-20 text-white rounded-lg hover:bg-opacity-30 transition-all transform hover:scale-105"
              >
                Dashboard
              </button>
              <button 
                onClick={() => setCurrentView('create-job')}
                className="px-4 py-2 bg-white bg-opacity-20 text-white rounded-lg hover:bg-opacity-30 transition-all transform hover:scale-105"
              >
                Create Job
              </button>
              <button 
                onClick={() => setCurrentView('candidates')}
                className="px-4 py-2 bg-white bg-opacity-20 text-white rounded-lg hover:bg-opacity-30 transition-all transform hover:scale-105"
              >
                Candidates
              </button>
              <button 
                onClick={() => setCurrentView('offers')}
                className="px-4 py-2 bg-white bg-opacity-20 text-white rounded-lg hover:bg-opacity-30 transition-all transform hover:scale-105"
              >
                Offers
              </button>
            </div>
          </div>
        </div>
      </nav>
      <main className="flex-1 max-w-7xl mx-auto w-full px-4 py-8">
        {renderView()}
      </main>
    </div>
  );
}

export default App;
