import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './CandidateList.css';

const CandidateList = ({ jobId, onSchedule }) => {
  const [jobs, setJobs] = useState([]);
  const [selectedJob, setSelectedJob] = useState(null);
  const [candidates, setCandidates] = useState([]);
  const [showApplicationForm, setShowApplicationForm] = useState(false);
  const [applicationForm, setApplicationForm] = useState({
    name: '',
    email: '',
    phone: '',
    resume: null,
  });

  useEffect(() => {
    fetchJobs();
  }, []);

  useEffect(() => {
    if (selectedJob) {
      fetchCandidates(selectedJob);
    }
  }, [selectedJob]);

  const fetchJobs = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/jobs');
      setJobs(response.data);

      if (response.data.length > 0) {
        setSelectedJob(response.data[0].id);
      }
    } catch (error) {
      console.error('Error fetching jobs:', error);
    }
  };

  const fetchCandidates = async (jobId) => {
    try {
      const response = await axios.get(
        `http://localhost:5000/api/candidates?job_id=${jobId}`
      );
      setCandidates(response.data);
    } catch (error) {
      console.error('Error fetching candidates:', error);
    }
  };

  const handleFileChange = (e) => {
    setApplicationForm({
      ...applicationForm,
      resume: e.target.files[0],
    });
  };

  const handleInputChange = (e) => {
    setApplicationForm({
      ...applicationForm,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmitApplication = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('resume', applicationForm.resume);
    formData.append('job_id', selectedJob);
    formData.append('name', applicationForm.name);
    formData.append('email', applicationForm.email);
    formData.append('phone', applicationForm.phone);

    try {
      await axios.post('http://localhost:5000/api/apply', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });

      alert('Application submitted successfully!');
      setShowApplicationForm(false);
      setApplicationForm({
        name: '',
        email: '',
        phone: '',
        resume: null,
      });

      fetchCandidates(selectedJob);
    } catch (error) {
      console.error('Error submitting application:', error);
      alert('Failed to submit application');
    }
  };

  const getPriorityColor = (priority) => {
    switch (priority) {
      case 'High':
        return 'priority-high';
      case 'Medium':
        return 'priority-medium';
      case 'Low':
        return 'priority-low';
      default:
        return 'priority-low';
    }
  };

  return (
    <div className="space-y-6">
      <div className="bg-white rounded-xl shadow-lg p-6">
        <div className="flex justify-between items-start gap-6 mb-6">
          <div className="flex-1">
            <h2 className="text-3xl font-bold text-gray-800 mb-4">Candidate Pipeline</h2>

            <div className="flex flex-col sm:flex-row gap-4 items-start sm:items-center">
              <label className="text-sm font-semibold text-gray-700">Select Job:</label>
              <select
                value={selectedJob || ''}
                onChange={(e) => setSelectedJob(e.target.value)}
                className="px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:border-blue-500 focus:ring-2 focus:ring-blue-200 bg-white"
              >
                {jobs.map((job) => (
                  <option key={job.id} value={job.id}>
                    {job.title} ({job.applications || 0} applications)
                  </option>
                ))}
              </select>
            </div>
          </div>

          <button
            onClick={() => setShowApplicationForm(!showApplicationForm)}
            className="px-6 py-3 bg-blue-600 text-white font-semibold rounded-lg hover:bg-blue-700 transition-all transform hover:scale-105 whitespace-nowrap"
          >
            {showApplicationForm ? 'Close Form' : '+ Test Application'}
          </button>
        </div>

        {showApplicationForm && (
          <div className="bg-gray-50 border-2 border-blue-200 rounded-lg p-6 mb-6">
            <h3 className="text-lg font-bold text-gray-800 mb-4">Submit Test Application</h3>

            <form onSubmit={handleSubmitApplication} className="space-y-4">
              <input
                type="text"
                name="name"
                placeholder="Full Name"
                value={applicationForm.name}
                onChange={handleInputChange}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:border-blue-500 focus:ring-2 focus:ring-blue-200 transition-all"
                required
              />

              <input
                type="email"
                name="email"
                placeholder="Email"
                value={applicationForm.email}
                onChange={handleInputChange}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:border-blue-500 focus:ring-2 focus:ring-blue-200 transition-all"
                required
              />

              <input
                type="tel"
                name="phone"
                placeholder="Phone"
                value={applicationForm.phone}
                onChange={handleInputChange}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:border-blue-500 focus:ring-2 focus:ring-blue-200 transition-all"
                required
              />

              <input
                type="file"
                accept=".pdf,.doc,.docx"
                onChange={handleFileChange}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:border-blue-500"
                required
              />

              <button 
                type="submit" 
                className="w-full px-6 py-2 bg-blue-600 text-white font-semibold rounded-lg hover:bg-blue-700 transition-colors"
              >
                Submit Application
              </button>
            </form>
          </div>
        )}
      </div>

      {candidates.length === 0 ? (
        <div className="bg-white rounded-xl shadow-lg p-12 text-center">
          <p className="text-gray-500 text-lg">
            No candidates yet. Submit a test application above!
          </p>
        </div>
      ) : (
        <div className="bg-white rounded-xl shadow-lg overflow-hidden">
          <div className="overflow-x-auto">
            <table className="w-full">
              <thead>
                <tr className="bg-gray-100 border-b-2 border-gray-300">
                  <th className="text-left py-4 px-6 font-semibold text-gray-700">Name</th>
                  <th className="text-left py-4 px-6 font-semibold text-gray-700">Email</th>
                  <th className="text-left py-4 px-6 font-semibold text-gray-700">Skills</th>
                  <th className="text-left py-4 px-6 font-semibold text-gray-700">Experience</th>
                  <th className="text-left py-4 px-6 font-semibold text-gray-700">Match Score</th>
                  <th className="text-left py-4 px-6 font-semibold text-gray-700">Priority</th>
                  <th className="text-left py-4 px-6 font-semibold text-gray-700">Status</th>
                  <th className="text-left py-4 px-6 font-semibold text-gray-700">Actions</th>
                </tr>
              </thead>

              <tbody>
                {candidates.map((candidate) => (
                  <tr key={candidate.id} className="border-b border-gray-200 hover:bg-gray-50 transition-colors">
                    <td className="py-4 px-6 text-gray-800 font-semibold">{candidate.name}</td>
                    <td className="py-4 px-6 text-gray-600">{candidate.email}</td>

                    <td className="py-4 px-6">
                      <div className="flex flex-wrap gap-2">
                        {candidate.skills.slice(0, 3).map((skill, idx) => (
                          <span key={idx} className="bg-blue-100 text-blue-800 px-3 py-1 rounded-full text-sm font-semibold">
                            {skill}
                          </span>
                        ))}

                        {candidate.skills.length > 3 && (
                          <span className="bg-gray-200 text-gray-800 px-3 py-1 rounded-full text-sm font-semibold">
                            +{candidate.skills.length - 3}
                          </span>
                        )}
                      </div>
                    </td>

                    <td className="py-4 px-6 text-gray-800">{candidate.experience_years} yrs</td>

                    <td className="py-4 px-6">
                      <div className="space-y-1">
                        <div className="w-24 bg-gray-200 rounded-full h-2">
                          <div
                            className="bg-green-500 h-2 rounded-full"
                            style={{ width: `${candidate.match_score}%` }}
                          />
                        </div>
                        <span className="text-sm font-semibold text-gray-700">{candidate.match_score}%</span>
                      </div>
                    </td>

                    <td className="py-4 px-6">
                      <span className={`px-4 py-2 rounded-full text-sm font-semibold ${getPriorityColor(candidate.priority)}`}>
                        {candidate.priority}
                      </span>
                    </td>

                    <td className="py-4 px-6 text-gray-800">{candidate.status}</td>

                    <td className="py-4 px-6">
                      <button
                        className="px-4 py-2 bg-blue-600 text-white text-sm rounded-lg hover:bg-blue-700 transition-colors"
                        onClick={() =>
                          alert(`View details for ${candidate.name}`)
                        }
                      >
                        View
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}
    </div>
  );
};

export default CandidateList;
