import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import '../styles/InterviewSession.css';

const InterviewSession = ({ token }) => {
  const [interviewData, setInterviewData] = useState(null);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [responses, setResponses] = useState({});
  const [codingCode, setCodingCode] = useState('');
  const [codingResult, setCodingResult] = useState(null);
  const [timeRemaining, setTimeRemaining] = useState(0);
  const [status, setStatus] = useState('loading'); // loading, started, in_progress, completed
  const [score, setScore] = useState(null);
  const [loading, setLoading] = useState(false);
  const timerRef = useRef(null);

  useEffect(() => {
    fetchInterviewStatus();
  }, [token]);

  const fetchInterviewStatus = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/interview/${token}/status`);
      setInterviewData(response.data);
      setStatus(response.data.status);
      
      if (response.data.status === 'completed') {
        setScore(response.data.interview_score);
      }
    } catch (error) {
      console.error('Error fetching interview:', error);
      setStatus('error');
    }
  };

  const startInterview = async () => {
    try {
      setLoading(true);
      const response = await axios.post(`http://localhost:5000/api/interview/${token}/start`);
      setInterviewData(response.data);
      setStatus('in_progress');
      
      // Set timer for total interview duration (65 minutes)
      setTimeRemaining(65 * 60); // 65 minutes in seconds
      startTimer();
    } catch (error) {
      console.error('Error starting interview:', error);
      alert('Failed to start interview');
    } finally {
      setLoading(false);
    }
  };

  const startTimer = () => {
    if (timerRef.current) clearInterval(timerRef.current);
    timerRef.current = setInterval(() => {
      setTimeRemaining(prev => {
        if (prev <= 1) {
          clearInterval(timerRef.current);
          completeInterview();
          return 0;
        }
        return prev - 1;
      });
    }, 1000);
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  const handleResponseChange = (e) => {
    setResponses({
      ...responses,
      [currentQuestionIndex]: e.target.value
    });
  };

  const submitCurrentResponse = async () => {
    const response = responses[currentQuestionIndex] || '';
    
    if (!response.trim()) {
      alert('Please provide a response before submitting');
      return;
    }

    try {
      setLoading(true);
      await axios.post(`http://localhost:5000/api/interview/${token}/submit-response`, {
        question_id: currentQuestionIndex,
        response: response
      });

      // Move to next question
      if (currentQuestionIndex < interviewData.questions.length - 1) {
        setCurrentQuestionIndex(currentQuestionIndex + 1);
      } else {
        // All questions answered, move to coding round
        alert('All questions answered! Now for the coding challenge.');
        setCurrentQuestionIndex('coding');
      }
    } catch (error) {
      console.error('Error submitting response:', error);
      alert('Failed to submit response');
    } finally {
      setLoading(false);
    }
  };

  const submitCodingSolution = async () => {
    if (!codingCode.trim()) {
      alert('Please write some code before submitting');
      return;
    }

    try {
      setLoading(true);
      const response = await axios.post(`/api/interview/${token}/submit-code`, {
        code: codingCode,
        language: 'python'
      });

      setCodingResult(response.data.evaluation);
      alert(`Code submitted! Score: ${response.data.evaluation.overall_score.toFixed(2)}%`);
    } catch (error) {
      console.error('Error submitting code:', error);
      alert('Failed to submit code');
    } finally {
      setLoading(false);
    }
  };

  const completeInterview = async () => {
    try {
      const response = await axios.post(`/api/interview/${token}/complete`);
      setStatus('completed');
      setScore(response.data.interview_score);
    } catch (error) {
      console.error('Error completing interview:', error);
    }
  };

  if (status === 'loading') {
    return (
      <div className="interview-container loading">
        <div className="spinner"></div>
        <p>Loading your interview...</p>
      </div>
    );
  }

  if (status === 'error') {
    return (
      <div className="interview-container error">
        <h2>‚ùå Interview Not Found</h2>
        <p>The interview link may be expired or invalid.</p>
      </div>
    );
  }

  if (status === 'generated' && !loading) {
    return (
      <div className="interview-container welcome">
        <div className="welcome-card">
          <h1>üéØ Welcome to AI Interview Round</h1>
          <p className="subtitle">Candidate: {interviewData?.candidate_name}</p>
          
          <div className="interview-info">
            <div className="info-item">
              <span className="icon">‚è±Ô∏è</span>
              <div>
                <strong>Duration</strong>
                <p>{interviewData?.started_at ? 'In Progress' : '65 minutes'}</p>
              </div>
            </div>
            
            <div className="info-item">
              <span className="icon">‚ùì</span>
              <div>
                <strong>Questions</strong>
                <p>4 Technical + 2 Behavioral + 1 Coding</p>
              </div>
            </div>
            
            <div className="info-item">
              <span className="icon">ü§ñ</span>
              <div>
                <strong>Format</strong>
                <p>AI-Powered Adaptive Interview</p>
              </div>
            </div>
          </div>

          <div className="instructions">
            <h3>üìã What to Expect:</h3>
            <ul>
              <li>Answer technical questions about your skills and experience</li>
              <li>Respond to behavioral questions about your work style</li>
              <li>Complete a coding challenge with test case evaluation</li>
              <li>Get real-time feedback on your coding solution</li>
              <li>Receive an instant score and report</li>
            </ul>
          </div>

          <button 
            className="btn btn-primary start-btn"
            onClick={startInterview}
            disabled={loading}
          >
            {loading ? 'Starting...' : 'Start Interview'}
          </button>
        </div>
      </div>
    );
  }

  if (status === 'in_progress') {
    return (
      <div className="interview-container">
        <div className="interview-header">
          <div className="progress-bar">
            <div 
              className="progress-fill"
              style={{
                width: currentQuestionIndex === 'coding' 
                  ? '100%' 
                  : `${((currentQuestionIndex + 1) / (interviewData?.questions?.length || 1)) * 100}%`
              }}
            ></div>
          </div>
          
          <div className="timer" style={{
            color: timeRemaining < 300 ? '#ef4444' : '#3b82f6'
          }}>
            ‚è±Ô∏è {formatTime(timeRemaining)}
          </div>
        </div>

        {currentQuestionIndex !== 'coding' && (
          <div className="question-card">
            <div className="question-number">
              Question {currentQuestionIndex + 1} of {interviewData?.questions?.length}
            </div>
            
            <h2 className="question-text">
              {interviewData?.questions?.[currentQuestionIndex]?.question || 'Question loading...'}
            </h2>

            {interviewData?.questions?.[currentQuestionIndex]?.context && (
              <div className="question-context">
                <strong>Context:</strong>
                <p>{interviewData.questions[currentQuestionIndex].context}</p>
              </div>
            )}

            <div className="response-section">
              <label>Your Response:</label>
              <textarea
                className="response-textarea"
                placeholder="Type your answer here..."
                value={responses[currentQuestionIndex] || ''}
                onChange={handleResponseChange}
                rows="6"
              />
              <div className="char-count">
                {(responses[currentQuestionIndex] || '').length} characters
              </div>
            </div>

            <div className="button-group">
              <button
                className="btn btn-secondary"
                onClick={() => setCurrentQuestionIndex(Math.max(0, currentQuestionIndex - 1))}
                disabled={currentQuestionIndex === 0 || loading}
              >
                ‚Üê Previous
              </button>
              
              <button
                className="btn btn-primary"
                onClick={submitCurrentResponse}
                disabled={loading}
              >
                {loading ? 'Submitting...' : 'Next Question ‚Üí'}
              </button>
            </div>
          </div>
        )}

        {currentQuestionIndex === 'coding' && (
          <div className="coding-challenge-card">
            <h2>üíª Coding Challenge</h2>
            
            <div className="coding-problem">
              <div className="problem-title">
                Problem: Two Sum
              </div>
              <div className="problem-description">
                <p>Given an array of integers <code>nums</code> and an integer <code>target</code>, return the indices of the two numbers that add up to the target.</p>
                <p>You may assume each input has exactly one solution, and you cannot use the same element twice.</p>
                
                <strong>Example:</strong>
                <pre>Input: nums = [2,7,11,15], target = 9
Output: [0,1]
Explanation: nums[0] + nums[1] == 9, so we return [0, 1].</pre>
              </div>
            </div>

            <div className="coding-editor">
              <label>Write your solution in Python:</label>
              <textarea
                className="code-textarea"
                placeholder="def twoSum(nums, target):&#10;    # Your code here&#10;    pass"
                value={codingCode}
                onChange={(e) => setCodingCode(e.target.value)}
                spellCheck="false"
              />
            </div>

            {codingResult && (
              <div className="coding-result">
                <h4>Evaluation Result:</h4>
                <div className={`result-badge ${codingResult.overall_score >= 70 ? 'pass' : 'fail'}`}>
                  Score: {codingResult.overall_score.toFixed(2)}%
                </div>
                <div className="test-results">
                  <p><strong>Tests Passed:</strong> {codingResult.passed_tests}/{codingResult.total_tests}</p>
                  <p><strong>Code Quality:</strong> {codingResult.code_quality_score?.toFixed(2)}%</p>
                </div>
              </div>
            )}

            <div className="button-group">
              <button
                className="btn btn-secondary"
                onClick={() => setCurrentQuestionIndex(interviewData.questions.length - 1)}
                disabled={loading}
              >
                ‚Üê Back to Questions
              </button>
              
              <button
                className="btn btn-primary"
                onClick={submitCodingSolution}
                disabled={loading}
              >
                {loading ? 'Evaluating...' : 'Submit Code'}
              </button>
            </div>

            <button
              className="btn btn-success finish-btn"
              onClick={completeInterview}
              disabled={loading}
            >
              üéâ Complete Interview
            </button>
          </div>
        )}
      </div>
    );
  }

  if (status === 'completed') {
    return (
      <div className="interview-container completed">
        <div className="completion-card">
          <div className="score-display">
            <div className="score-circle">
              {score?.toFixed(2) || 'N/A'}%
            </div>
          </div>

          <h1>üéâ Interview Complete!</h1>
          <p className="subtitle">Thank you for completing the AI interview round.</p>

          <div className="score-breakdown">
            <h3>Your Score Breakdown:</h3>
            <div className="score-item">
              <span>Coding Ability</span>
              <div className="score-bar">
                <div className="score-fill" style={{width: '75%'}}></div>
              </div>
              <span className="score-value">75%</span>
            </div>
            <div className="score-item">
              <span>Response Quality</span>
              <div className="score-bar">
                <div className="score-fill" style={{width: '70%'}}></div>
              </div>
              <span className="score-value">70%</span>
            </div>
            <div className="score-item">
              <span>Communication</span>
              <div className="score-bar">
                <div className="score-fill" style={{width: '72%'}}></div>
              </div>
              <span className="score-value">72%</span>
            </div>
            <div className="score-item">
              <span>Behavioral Fit</span>
              <div className="score-bar">
                <div className="score-fill" style={{width: '68%'}}></div>
              </div>
              <span className="score-value">68%</span>
            </div>
          </div>

          <div className="next-steps">
            <h3>üìß What's Next?</h3>
            <p>Our team will review your interview response within 24-48 hours. If you move forward, you'll receive an email with details about the next round.</p>
            <p>Check your email regularly for updates!</p>
          </div>

          <a href="/" className="btn btn-primary">
            Return to Portal
          </a>
        </div>
      </div>
    );
  }

  return null;
};

export default InterviewSession;
