import React, { useState, useEffect } from 'react';
import './OfferManagement.css';

function OfferManagement({ onBack }) {
  const [offers, setOffers] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({
    candidateId: '',
    jobId: '',
    salaryOffered: '',
    startDate: '',
    benefits: '',
    notes: ''
  });

  useEffect(() => {
    // TODO: Fetch existing offers from backend
    fetchOffers();
  }, []);

  const fetchOffers = async () => {
    // Mock data
    setOffers([
      {
        id: 'O001',
        candidateName: 'John Doe',
        jobTitle: 'Software Engineer',
        salary: '$120,000',
        status: 'pending',
        createdDate: '2024-01-15'
      },
      {
        id: 'O002',
        candidateName: 'Jane Smith',
        jobTitle: 'Backend Engineer',
        salary: '$140,000',
        status: 'accepted',
        createdDate: '2024-01-10'
      }
    ]);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // TODO: Send offer creation request to backend
    console.log('Creating offer:', formData);
    setShowForm(false);
    setFormData({
      candidateId: '',
      jobId: '',
      salaryOffered: '',
      startDate: '',
      benefits: '',
      notes: ''
    });
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'accepted':
        return 'status-accepted';
      case 'pending':
        return 'status-pending';
      case 'rejected':
        return 'status-rejected';
      default:
        return 'status-pending';
    }
  };

  return (
    <div className="offer-management-container">
      {onBack && (
        <button 
          onClick={onBack}
          className="offer-back-button"
        >
          ‚Üê Back
        </button>
      )}

      <div className="offer-card">
        <div className="offer-header">
          <h1 className="offer-title">Offer Management</h1>
          <button 
            className="btn-create-offer"
            onClick={() => setShowForm(!showForm)}
          >
            {showForm ? 'Cancel' : 'Create New Offer'}
          </button>
        </div>

        {showForm && (
          <form onSubmit={handleSubmit} className="offer-form">
            <div className="form-group">
              <label htmlFor="candidateId" className="form-label">Candidate</label>
              <select
                id="candidateId"
                name="candidateId"
                value={formData.candidateId}
                onChange={handleChange}
                className="form-select"
                required
              >
                <option value="">Select Candidate</option>
                <option value="C001">John Doe</option>
                <option value="C002">Jane Smith</option>
              </select>
            </div>

            <div className="form-group">
              <label htmlFor="jobId" className="form-label">Job Position</label>
              <select
                id="jobId"
                name="jobId"
                value={formData.jobId}
                onChange={handleChange}
                className="form-select"
                required
              >
                <option value="">Select Job</option>
                <option value="J001">Software Engineer</option>
                <option value="J002">Backend Engineer</option>
              </select>
            </div>

            <div className="form-row">
              <div className="form-group">
                <label htmlFor="salaryOffered" className="form-label">Salary Offered</label>
                <input
                  type="number"
                  id="salaryOffered"
                  name="salaryOffered"
                  value={formData.salaryOffered}
                  onChange={handleChange}
                  placeholder="e.g., 120000"
                  className="form-input"
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="startDate" className="form-label">Start Date</label>
                <input
                  type="date"
                  id="startDate"
                  name="startDate"
                  value={formData.startDate}
                  onChange={handleChange}
                  className="form-input"
                  required
                />
              </div>
            </div>

            <div className="form-group">
              <label htmlFor="benefits" className="form-label">Benefits</label>
              <textarea
                id="benefits"
                name="benefits"
                value={formData.benefits}
                onChange={handleChange}
                placeholder="List benefits offered"
                rows="3"
                className="form-textarea"
              />
            </div>

            <div className="form-group">
              <label htmlFor="notes" className="form-label">Notes</label>
              <textarea
                id="notes"
                name="notes"
                value={formData.notes}
                onChange={handleChange}
                placeholder="Additional notes"
                rows="3"
                className="form-textarea"
              />
            </div>

            <div className="form-button-group">
              <button 
                type="submit" 
                className="btn-submit"
              >
                Create Offer
              </button>
              <button 
                type="button"
                onClick={() => setShowForm(false)}
                className="btn-cancel-form"
              >
                Cancel
              </button>
            </div>
          </form>
        )}
      </div>

      <div className="offer-card">
        <h3 className="offers-title">Offers List</h3>
        <div style={{ overflowX: 'auto' }}>
          <table className="offers-table">
            <thead>
              <tr>
                <th>Candidate</th>
                <th>Job Title</th>
                <th>Salary</th>
                <th>Status</th>
                <th>Created Date</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {offers.map(offer => (
                <tr key={offer.id}>
                  <td style={{ fontWeight: '600' }}>{offer.candidateName}</td>
                  <td>{offer.jobTitle}</td>
                  <td style={{ fontWeight: '600' }}>{offer.salary}</td>
                  <td>
                    <span className={`status-badge ${getStatusColor(offer.status)}`}>
                      {offer.status.charAt(0).toUpperCase() + offer.status.slice(1)}
                    </span>
                  </td>
                  <td>{offer.createdDate}</td>
                  <td>
                    <div className="offer-actions">
                      <button className="btn-action">
                        View
                      </button>
                      <button className="btn-action">
                        Edit
                      </button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default OfferManagement;
