import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Dashboard.css';

const Dashboard = ({ onNavigate }) => {
  const [stats, setStats] = useState({
    total_jobs: 0,
    total_applications: 0,
    high_priority: 0,
    medium_priority: 0,
    recent_applications: []
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchStats();
  }, []);

  const fetchStats = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/dashboard/stats');
      setStats(response.data);
      setError(null);
    } catch (error) {
      console.error('Error fetching stats:', error);
      // Set default stats on error
      setStats({
        total_jobs: 0,
        total_applications: 0,
        high_priority: 0,
        medium_priority: 0,
        recent_applications: []
      });
      setError('Unable to fetch dashboard stats. Using default values.');
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <div className="text-center py-8 text-gray-600 text-lg">Loading...</div>;

  return (
    <div className="space-y-8">
      <div className="flex justify-between items-center">
        <h2 className="text-3xl font-bold text-gray-800">Recruitment Dashboard</h2>
        <button 
          onClick={() => fetchStats()}
          className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
        >
          Refresh
        </button>
      </div>

      {error && (
        <div className="bg-yellow-50 border border-yellow-200 text-yellow-800 px-4 py-3 rounded-lg">
          {error}
        </div>
      )}
      
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <div className="bg-gradient-to-br from-blue-500 to-blue-600 text-white p-6 rounded-xl shadow-lg hover:shadow-xl transform hover:-translate-y-1 transition-all">
          <h3 className="text-4xl font-bold">{stats.total_jobs}</h3>
          <p className="text-blue-100 mt-2">Total Jobs</p>
        </div>
        <div className="bg-gradient-to-br from-green-500 to-green-600 text-white p-6 rounded-xl shadow-lg hover:shadow-xl transform hover:-translate-y-1 transition-all">
          <h3 className="text-4xl font-bold">{stats.total_applications}</h3>
          <p className="text-green-100 mt-2">Applications</p>
        </div>
        <div className="bg-gradient-to-br from-red-500 to-red-600 text-white p-6 rounded-xl shadow-lg hover:shadow-xl transform hover:-translate-y-1 transition-all">
          <h3 className="text-4xl font-bold">{stats.high_priority}</h3>
          <p className="text-red-100 mt-2">High Priority</p>
        </div>
        <div className="bg-gradient-to-br from-yellow-500 to-yellow-600 text-white p-6 rounded-xl shadow-lg hover:shadow-xl transform hover:-translate-y-1 transition-all">
          <h3 className="text-4xl font-bold">{stats.medium_priority}</h3>
          <p className="text-yellow-100 mt-2">Medium Priority</p>
        </div>
      </div>

      <div className="bg-white rounded-xl shadow-lg p-8">
        <h3 className="text-2xl font-bold text-gray-800 mb-6">Recent Applications</h3>
        {stats.recent_applications && stats.recent_applications.length > 0 ? (
          <div className="overflow-x-auto">
            <table className="w-full">
              <thead>
                <tr className="border-b-2 border-gray-300">
                  <th className="text-left py-3 px-4 font-semibold text-gray-700">Name</th>
                  <th className="text-left py-3 px-4 font-semibold text-gray-700">Skills</th>
                  <th className="text-left py-3 px-4 font-semibold text-gray-700">Match Score</th>
                  <th className="text-left py-3 px-4 font-semibold text-gray-700">Priority</th>
                  <th className="text-left py-3 px-4 font-semibold text-gray-700">Status</th>
                </tr>
              </thead>
              <tbody>
                {stats.recent_applications.map(app => (
                  <tr key={app.id} className="border-b border-gray-200 hover:bg-gray-50 transition-colors">
                    <td className="py-3 px-4 text-gray-800">{app.name}</td>
                    <td className="py-3 px-4">
                      <div className="flex gap-2 flex-wrap">
                        {app.skills.map(skill => (
                          <span key={skill} className="bg-blue-100 text-blue-800 px-2 py-1 rounded text-sm">
                            {skill}
                          </span>
                        ))}
                      </div>
                    </td>
                    <td className="py-3 px-4">
                      <div className="w-full bg-gray-200 rounded-full h-2">
                        <div 
                          className="bg-green-500 h-2 rounded-full" 
                          style={{width: `${app.match_score}%`}}
                        ></div>
                      </div>
                      <span className="text-sm text-gray-600">{app.match_score}%</span>
                    </td>
                    <td className="py-3 px-4">
                      <span className={`px-3 py-1 rounded-full text-sm font-semibold ${
                        app.priority === 'High' ? 'bg-red-100 text-red-800' :
                        app.priority === 'Medium' ? 'bg-yellow-100 text-yellow-800' :
                        'bg-green-100 text-green-800'
                      }`}>
                        {app.priority}
                      </span>
                    </td>
                    <td className="py-3 px-4 text-gray-800">{app.status}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ) : (
          <p className="text-gray-500 text-center py-6">No applications yet</p>
        )}
      </div>

      <div className="bg-white rounded-xl shadow-lg p-8">
        <h3 className="text-2xl font-bold text-gray-800 mb-6">Quick Actions</h3>
        <div className="flex gap-4 flex-wrap">
          <button 
            onClick={() => onNavigate('create-job')} 
            className="px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-all transform hover:scale-105 font-semibold"
          >
            + Create New Job
          </button>
          <button 
            onClick={() => onNavigate('candidates')} 
            className="px-6 py-3 bg-gray-600 text-white rounded-lg hover:bg-gray-700 transition-all transform hover:scale-105 font-semibold"
          >
            View All Candidates
          </button>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
