import React, { useState } from 'react';
import './InterviewScheduler.css';

function InterviewScheduler({ onBack }) {
  const [formData, setFormData] = useState({
    candidateId: '',
    jobId: '',
    interviewDate: '',
    interviewTime: '',
    interviewType: 'phone',
    interviewer: '',
    notes: ''
  });
  const [submitted, setSubmitted] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Scheduling interview:', formData);
    setSubmitted(true);
    setTimeout(() => {
      setSubmitted(false);
      if (onBack) onBack();
    }, 2000);
  };

  if (submitted) {
    return (
      <div className="success-message" style={{ maxWidth: '800px', margin: '0 auto' }}>
        <h2 className="success-title">✅ Interview Scheduled!</h2>
        <p>Redirecting...</p>
      </div>
    );
  }

  return (
    <div className="interview-scheduler-container">
      {onBack && (
        <button 
          onClick={onBack}
          className="interview-back-button"
        >
          ← Back
        </button>
      )}
      
      <div className="interview-card">
        <h1 className="interview-title">Schedule Interview</h1>
        <form onSubmit={handleSubmit} className="interview-form">
          <div className="form-group">
            <label htmlFor="candidateId" className="form-label">Candidate</label>
            <select
              id="candidateId"
              name="candidateId"
              value={formData.candidateId}
              onChange={handleChange}
              className="form-select"
              required
            >
              <option value="">Select Candidate</option>
              <option value="C001">John Doe</option>
              <option value="C002">Jane Smith</option>
            </select>
          </div>

          <div className="form-group">
            <label htmlFor="jobId" className="form-label">Job Position</label>
            <select
              id="jobId"
              name="jobId"
              value={formData.jobId}
              onChange={handleChange}
              className="form-select"
              required
            >
              <option value="">Select Job</option>
              <option value="J001">Software Engineer</option>
              <option value="J002">Backend Engineer</option>
            </select>
          </div>

          <div className="form-row">
            <div className="form-group">
              <label htmlFor="interviewDate" className="form-label">Interview Date</label>
              <input
                type="date"
                id="interviewDate"
                name="interviewDate"
                value={formData.interviewDate}
                onChange={handleChange}
                className="form-input"
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="interviewTime" className="form-label">Interview Time</label>
              <input
                type="time"
                id="interviewTime"
                name="interviewTime"
                value={formData.interviewTime}
                onChange={handleChange}
                className="form-input"
                required
              />
            </div>
          </div>

          <div className="form-group">
            <label htmlFor="interviewType" className="form-label">Interview Type</label>
            <select
              id="interviewType"
              name="interviewType"
              value={formData.interviewType}
              onChange={handleChange}
              className="form-select"
            >
              <option value="phone">Phone Screen</option>
              <option value="video">Video Call</option>
              <option value="in-person">In-Person</option>
              <option value="technical">Technical Interview</option>
            </select>
          </div>

          <div className="form-group">
            <label htmlFor="interviewer" className="form-label">Interviewer</label>
            <input
              type="text"
              id="interviewer"
              name="interviewer"
              value={formData.interviewer}
              onChange={handleChange}
              placeholder="Interviewer name"
              className="form-input"
            />
          </div>

          <div className="form-group">
            <label htmlFor="notes" className="form-label">Notes</label>
            <textarea
              id="notes"
              name="notes"
              value={formData.notes}
              onChange={handleChange}
              placeholder="Additional notes"
              rows="4"
              className="form-textarea"
            />
          </div>

          <button 
            type="submit" 
            className="btn-submit"
            style={{ width: '100%' }}
          >
            Schedule Interview
          </button>
        </form>
      </div>
    </div>
  );
}
export default InterviewScheduler;
